insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('雷洁营养师','true','#小雷话营养# 【香椿】香椿是春季的时令名品，含香椿素等挥发性芳香族有机物，具有清热利湿、利尿解毒的功效，可以辅助防治肠炎、痢疾、泌尿系统感染等疾病。香椿中硝酸盐和亚硝酸盐含量高于一般蔬菜， 因此食用之前在沸水中汆烫1分钟左右，不但可以除去大部分的亚硝酸盐和硝酸盐，还可保留香椿的本色。','2015-03-18 22:40','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('雷洁营养师','true','#小雷话营养# 【海带】 海带是一种营养价值、药用价值很高的蔬菜。海带含碘丰富，具有预防缺碘性甲状腺肿的作用。海带所含有硒能有效清除人体内自由基，海带具有延缓衰老的作用。海带还含有甘露醇，有降低血压、利尿和消肿的功效。由于海带中碘含量较为丰富，因此，甲亢患者、孕妇和乳母不宜过多食用。','2015-03-17 23:39','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('雷洁营养师','true','#小雷话营养# 【鱼腥草】 鱼腥草是中国药典收录的一味草药。此外，鱼腥草还是一种比较常见的野生蔬菜。主含挥发油，癸酰乙醛鱼腥草素等多种成分，有清热解毒、排痛消肿疗疮、利尿除湿、健胃消食、增强提高人体免疫力等功效。新鲜的鱼腥草可泡水当茶饮，烹食炒熟或凉拌当菜吃。也是南方地区常用的佐料。','2015-03-15 23:06','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('雷洁营养师','true','#小雷话营养#【梨】 惊蛰时节，乍暖还寒，除了注意防寒保暖，还因气候干燥，很容易使人口干舌燥、外感咳嗽。新鲜的梨含水量达83%，热量主要来源是碳水化合物。梨的果肉中含有丰富的果浆，葡萄糖和苹果酸等有机酸。梨具有祛痰止咳的功效，对咽喉有养护的作用。梨含有较高的糖分，因此糖尿病患者慎食。','2015-03-06 19:20','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('雷洁营养师','true','#小雷话营养#【竹笋】竹笋在中国自古被当作“菜中珍品”。竹笋含有丰富的蛋白质、钙、磷、铁、胡萝卜素、维生素B1、B2、维生素C和胡萝卜素。竹笋中蛋白质比较丰富，含有丰富的人体必需氨基酸。竹笋低脂肪、低糖、多纤维，经常食用竹笋能促进肠道蠕动，帮助消化，去积食帮助消化，有预防大肠癌的功效。','2015-02-17 20:42','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师李新利','false','热门 夜话莲藕:1.含大量蛋白质，淀粉，脂肪，糖类，维生素B，维生素C及钙，磷，铁等多种矿物质。2.莲藕具有凉血止血，除烦解渴，养血生机的功效。主治肺热咳嗽，烦躁口渴，脾虚泄泻，食欲不振等。','2015-03-06 22:57','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师李新利','false','香蕉不宜空腹吃:因为香蕉中含有较多的镁，空腹食用会使人体中的镁突然升高而对心血管产生抑制作用，不利于身体健康。','2015-03-22 08:03','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师李新利','false','黄瓜的健康吃法:黄瓜不宜去皮去籽吃，有人吃黄瓜时习惯削皮去籽，其实黄瓜皮含有丰富的胡萝卜素，黄瓜籽中含有较丰富的维生素E，维生素C等成分，如果削皮去籽就损失很多宝贵的营养成分。','2015-03-17 23:34','4','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师李新利','false','玉米的健康吃法:1.吃鲜玉米要带着胚尖吃。玉米胚尖含有丰富的物质，可增强人体新陈代谢。使皮肤光滑细嫩，延缓皱纹的产生，所以吃玉米的时候一定不要舍弃胚尖。2.煮玉米粥时放点碱。因为玉米的烟酸是结合型的，在碱性环境中容被分离出来，容易被人体吸收。','2015-03-16 21:22','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师李新利','false','选购马铃薯的诀窍:1.挑选个大形正的马铃薯。2.马铃薯变绿和发芽时有毒物质会增加，尽量不要购买。3.选择皮面光滑而不过厚，芽眼较浅以便于削皮的马铃薯。4.选择无机械损伤，不带毛根，无虫蛀孔洞的马铃薯。','2015-03-08 18:24','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('晓淇营养师','false','【为了肝，莫生气食青色】别人气我我不气，生气发怒损肝脏，损了肝脏害健康，食疗养肝时刻记，少吃油炸难消化，vc食物助排毒，养肝护肝很神奇，青色食物养护肝，促进肝气的循环，舒缓肝郁护视力，花椰菜和绿叶菜，黄瓜芹菜包心菜，菠菜海带也不错，每日交换着食用，春天少酸多甘味，养肝护肝益处多。','2015-03-22 18:03','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('晓淇营养师','false','【选对食物好睡眠】 失眠已经成为一个影响人们健康的重要问题。厨房里的洋葱不只是我们的食物，提供给我们很好的营养，维护我们的心血管健康，也是很好的助眠食物，它们的气味具有安神、诱人入眠的作用，可以使大脑的皮层受到抑制。将洋葱切碎放入小瓶内，睡前闻一闻， 10分钟后即可安然入睡。','2015-03-21 16:55','2','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('晓淇营养师','false','#嘴巴停不住#【好气色葡萄干】 不少干果都有其促进人体健康的绝招， 葡萄干中的铁和钙含量十分丰富，还含有多种矿物质和维生素、氨基酸，对神经衰弱和过度疲劳者有较好的补益作用。葡萄干中含有的葡萄糖，对心肌有营养作用，有助于冠心病患者的康复。每天吃一小把，可以缓解手脚冰冷、贫血等现象。','2015-03-15 16:50','1','0','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('晓淇营养师','false','#晓淇话营养#【饱饱的午餐】经过上午的工作和学习，早餐的能量已被消耗，午餐的能量占全天的30%～40%， 健康的午餐应以五谷为主，配合大量蔬菜、瓜果，适量肉类、蛋类及鱼类，还需注意低油、低盐、低糖及高纤维。还得讲究123的比例:六分之一是肉或鱼或蛋类，六分之二是蔬菜水果，六分之三是饭或面。','2015-03-10 13:12','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('晓淇营养师','false','#晓淇话营养#【惊蛰你吃梨了吗】惊蛰时节，气候比较干燥，很容易使人口干舌燥引起咳嗽。惊蛰有吃梨的习俗 ，生梨性寒味甘，有润肺止咳、滋阴清热的功效。梨生食熟吃都可以，冰糖蒸梨对咳嗽具有很好的疗效，而且制作简单方便，平时也可以把其当作甜点食用，即润了肺也解了馋一举两得啊！','2015-03-06 09:44','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('晓淇营养师','false','#晓淇话营养#【洋葱】洋葱中不仅富含维生素C、叶酸、钾、锌、硒等营养素。更有两种特殊的营养物质——槲皮素和前列腺素A，含有其他食物不可替代的健康功效，杀菌抗感冒、预防癌症、保护心血管、清除体内氧自由基，增强新陈代谢能力，抗衰老，预防骨质疏松，是非常适合中老年人的保健食物。','2015-03-01 23:59','2','0','0','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('乔乔麻麻的happiness','false','热门 #乔妈哟话说# 【预防感冒吃点啥 中篇】锌可以对抗感冒，维生素C可帮忙，海鲜牡蛎富含锌，柑橘水果应季吃，补维生素胡萝卜素；可适量吃深海鱼，含ω-3脂肪酸，提免疫还消炎症，抗击感冒它也行；蓝莓紫薯味道好，还含花青素很多，也是抗氧化好手；饭后来小盒酸奶，可保护肠道健康，防上呼吸道感染；','2015-03-06 22:35','2','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('乔乔麻麻的happiness','false','#话·节# 【今天您睡得好吗】321一堆节日，我来说说睡眠日。睡前来杯热牛奶，色氨酸促进大脑产生五羟色胺，肽累可解除疲劳以尽快入睡；睡前四小时尽量避免进食大量食物，以免造成肠胃负担，从而影响睡眠；睡姿合适调整心态，睡前稍微做些舒缓的运动，以促进睡眠，或者洗个热水澡，除尘解乏，提高睡眠质量','2015-03-21 16:35','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('乔乔麻麻的happiness','false','#乔妈哟话说#【怎样有好牙口】牙好身体才能好，牙主要成分是钙，少吃甜常喝牛奶，洋葱，芥末和香菇，抑制细菌影响牙，吃些芹菜粗纤维，可清理食物残渣，胡萝卜黄瓜和笋，吃了能按摩牙龈；饭后漱口清口腔，吃粒无糖木糖醇，每天抽空喝点茶，氟和儿茶酚抗龋；坚持早晚都刷牙，每三个月换牙刷，牙好胃口好','2015-03-10 16:45','3','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('乔乔麻麻的happiness','false','#乔妈哟话说#【预防感冒吃点啥 中篇】锌可以对抗感冒，维生素C可帮忙，海鲜牡蛎富含锌，柑橘水果应季吃，补维生素胡萝卜素；可适量吃深海鱼，含ω-3脂肪酸，提免疫还消炎症，抗击感冒它也行；蓝莓紫薯味道好，还含花青素很多，也是抗氧化好手；饭后来小盒酸奶，可保护肠道健康，防上呼吸道感染；','2015-03-06 22:35','2','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('乔乔麻麻的happiness','false','#乔妈哟话说#【预防感冒吃点啥 上篇】近日昼夜温差大，空气质量也欠佳，嗓子肿痛鼻子堵，赶紧吃这些食物，增强免疫防感冒：大蒜含有硫化物，大蒜素杀菌消毒，还产生抗氧化剂，记得吃完喝点奶，可以消除尴尬味；喝牛奶可以补钙，保骨骼健康护心脏，还能增强免疫力；要多吃深色蔬菜，抗氧化成分很多；','2015-03-06 22:32','1','0','1','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('乔乔麻麻的happiness','false','#乔妈哟话说#【好眼睛爱“吃”】眼睛是心灵窗户，必须要好好呵护。吃胡萝卜西红柿，补充维生素A原，动物肝脏鱼和蛋，补充维生素AD，防止夜盲和干眼；吃葵花籽卷心菜，维生素E抗氧化，大蒜洋葱降眼压，可以预防青光眼；多吃鱼类核桃油，含ω－3脂肪酸，降黄斑变性风险；食物种类多而艳，并多饮水才最赞','2015-02-28 18:55','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师燕艳','false','#菜营养# 中国居民膳食指南建议每天摄入蔬菜300克～500克，蔬菜种类繁多，选择不同品种合理搭配才更有利于健康。①选择新鲜和应季蔬菜；②条件允许的话选择尽可能多种类；③深色蔬菜占蔬菜摄入量的一半；④注意增加十字花科蔬菜、菌藻类的摄入；⑤腌菜和酱菜少吃；⑥吃含淀粉高的蔬菜应适当减少主食。','2015-03-11 21:46','7','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师燕艳','false','#豆营养#【黑豆】黑豆豆浆，原料：黑豆、砂糖、水，做法：先将黑豆在温水中浸泡6-7小时，然后放入豆浆机中加水打成豆浆。豆浆打好后加入适量砂糖，也可不加。功效：降血脂、预防心血管疾病、抗氧化，延缓老化、养颜美容，增加肠胃蠕动。可以在早餐时搭配全麦面包、蔬菜食用，有减肥作用。','2015-02-06 10:16','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师燕艳','false','#冬吃萝卜#【白萝卜吃法5】白萝卜煮粥，①可以促进消化，减少消化不良和胃酸过多等症状，治疗积食和腹胀；②可起到消炎、化痰、清除内热的作用；③经常食用白萝卜粥可以增强人体免疫力，有效减少感冒的发生；④白萝卜有利水消肿作用，白萝卜粥也是应对人体浮肿有效的食疗佳品。 @Nutrition高雅','2015-01-26 11:17','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','热门 #说健康# 【三月菠萝正当季】菠萝几乎含所有人体所需的营养素,其含有的蛋白酶能分解食物中的蛋白质,餐后食用可开胃、去油腻助消化.菠萝中所含的糖、酶有一定的利尿作用,对肾炎和高血压者有益,对支气管炎也有辅助疗效.菠萝以3-8月品质佳.鲜食时需用淡盐水侵泡30分钟,以去除果肉中皂素,减少对口腔的刺激.','2015-03-11 11:10','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康# 【竹笋食用注意】竹笋适合炒、烧、拌、炝和配菜拌馅等。食用前应先用开水焯过，以去除草酸。靠近笋尖部位宜顺切，下部宜横切。这样烹调时不仅容易煮烂还入味。鲜竹笋存放不要剥壳以保存清香味。竹笋和肉同食可帮助解油腻,避免摄入过多油脂。','2015-03-17 09:33','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','【菜中珍品～竹笋】竹笋中含氮物质构成了竹笋独特的清香,具有开胃助消化增进食欲功效.竹笋中植物纤维可增加肠蠕动,治疗便秘.竹笋具有低糖、低脂特点,含植物纤维,可促进体内多余脂肪排除,消痰化淤,缓解高血压、高血脂、高血糖症,对消化道癌肿及乳腺癌有一定的预防作用.竹笋还能增强免疫力提高抗病能力.','2015-03-17 09:14','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康# 【草莓与健康】草莓鲜美红嫩,果肉多汁,酸甜可口,香味浓郁,色香味俱佳.对胃肠道和贫血有一定的滋补调养作用,对动脉硬化、冠心病也有好处,同时还有防癌和清除体内重金属离子作用.清洗时不要摘蒂,在流动水下冲洗,洗后再用盐水泡大约5分钟,这样维C等营养成分不被破坏,还能抑制表面的细菌等微生物.','2015-03-07 12:40','2','0','0','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','【水果与健康】水果含有人体需要的多种维生素,矿物质,碳水化合物,膳食纤维,果胶,有机酸等多种营养成分,既香甜可口又促进代谢,预防疾病.膳食纤维有利肠蠕动,促进体内废物排泄.餐前吃水果可控制进餐总量（部分除外）,利于水溶性维生素消化吸收.雾霾天除注意防护外,饮食宜清淡,适量新鲜水果更利于健康.','2015-03-02 22:55','4','0','0','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','【黄金宝瓜-南瓜】南瓜含丰富的维生素和果胶,尤其胡萝卜素含量较高.南瓜心胡萝卜素含量相当于果肉5倍.南瓜有防治糖尿病,降低血糖作用.其果胶能黏结和消除体内毒素和有害物质还可保护胃肠道黏膜.南瓜还有防癌.增强肝肾细胞再生.促进肠蠕动.造血.驱虫和帮助消化等作用.但黄疸患者不宜食用.其籽.花也可食.','2015-03-01 21:35','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','【藕】藕又称莲菜、莲藕等.含淀粉、蛋白质、天冬氨酸、维C及氧化酶等,含糖量也很高.生食或打汁能清热解烦,解渴止呕,凉血止血;熟藕能健脾开胃、益血补心,主补五脏.可消食、止渴、生津.其丰富的膳食纤维益于肠道健康.藕还适宜贫血及营养不良者食.生藕性寒,脾胃虚寒.女子经期忌食,熟藕.藕粉糖尿病者忌食.','2015-02-22 22:13','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康# 【香蕉与高血压】高血压和心脑血管疾病患者体内往往“钠”多而“钾”少，香蕉中含有丰富的钾元素，钾离子有抑制钠离子收缩血管和损坏心血管的作用。吃香蕉可维持体内的钠钾平衡和酸碱平衡，维持细胞内正常渗透压、使神经肌肉保持正常、心肌收缩协调，对高血压及心脑血管疾病的患者有益。','2015-02-22 13:39','3','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康#【春节饮食健康】过年走家串户，别忘健康饮食。少肉多菜更健康，水果薯类别忘记，低脂低盐防疾病，食不过量要运动，小心节后变胖子。瓜子花生等干果，适量吃点有好处。喝酒注意要低度，为了健康要少量。饭菜再香要七分，饮料味好水健康，新鲜卫生保平安。','2015-02-19 09:43','1','0','0','6','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康# 【饮酒应限量】无节制的饮酒会使食欲下降，食物摄入量减少，会发生多种营养素缺乏、急慢性酒精中毒、酒精性脂肪肝、严重时还会引起酒精性肝硬化。过量饮酒还会增加患高血压、心血管等疾病危险，也可能导致事故和暴力事件，对个人健康和社会安定都是不利的。如需饮酒尽量饮低度酒，并控制量。','2015-02-15 23:38','0','4','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康# 【大豆食用宜忌】大豆适合脾虚气弱、消瘦少食者食用；适宜贫血、营养不良者食用；适宜高血压等心血管病人食用；适宜糖尿病、肥胖者食用。大豆碳水化合物中纤维素和可溶性糖多，消化过程中会引起肠道胀气。肠胃不适、痛风患者不宜食用过多。大豆可发豆芽、做粥、磨粉、做豆浆、豆腐、面点等。','2015-02-11 13:07','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康# 【植物肉-大豆】大豆按颜色分为黄大豆、青大豆、黑大豆、紫大豆等，营养价值大致相同。大豆蛋白质含量高达35%-40%，有植物肉之称。大豆与谷类混合食用，可以较好的发挥蛋白质的互补作用。常食可减轻血管系统破坏、预防骨质疏松、乳腺癌和前列腺癌的发生，有益于神经、血管、大脑的发育生长。','2015-02-11 12:51','8','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康# 【五谷养五脏】①小米养脾，五谷之首，补中益气，为脾胃进补上品②大米润肺，滋阴润肺，兼有养胃之功③小麦养心，长于秋冬春夏具备四季精华，称五谷志贵，养心安神，除烦祛躁④大豆养肾，黑豆是肾之谷，补肾强身，解毒、润肤，尤适合肾虚者⑤高粱养肝，磨面焙黄熬粥服用，保肝益胃。','2015-02-10 13:01','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康# 【洋葱清脂降压】洋葱中含有的前列腺素A能扩张血管、降低血液黏度，因而会产生降血压、减少外周血管和增加冠状动脉的血流量，有预防血栓形成的作用。能促进钠盐排泄、能抑制高脂肪引起的胆固醇升高。常食对高血压、高血脂、心脑血管病人有保健作用。瘙痒性皮肤病、急性眼疾出血者勿食。','2015-02-08 11:49','2','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘宏微语','false','#说健康# 【洋葱抗癌】洋葱和其家族成员可以帮助预防和控制各种癌症。洋葱的防癌功效来自于它富含的硒元素和槲皮素。硒是一种抗氧化剂，能刺激人体免疫反应，从而抑制癌细胞的分裂和生长，同时还可降低致癌物的毒性。而槲皮素则能抑制致癌细胞活性，阻止癌细胞生长。常吃洋葱者患癌几率少25%-30%。','2015-02-08 10:58','4','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('张明营养师','false','#小明谝营养#【温馨提醒】年底了，大家都合家团聚，各种聚会的场合，大部分人都免不了把别人喝多或被别人喝多，这种行为是极其不健康的，应该杜绝。根据《中国居民膳食指南》中明确建议成年男性一天饮用酒的酒精量不超过25g，成年女性一天饮用酒的酒精量不超过15g。保护好自己的身体才是王道！','2015-02-12 23:15','0','4','0','1','wine');
