insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容健康必看','false','【学生应该多吃的8种食物】1、胡萝卜：加快大脑的新陈代谢； 2、菠萝：提高学习效率； 3、白菜：能减少紧张情绪； 4、柠檬：有助于集中精力； 5、海虾：长时间保持精力集中； 6、洋葱头：改善大脑氧的供应状况 ；7、香蕉：含血清素给大脑供养； 8、海带：缓解学习后的疲劳感。','2014-12-25 17:24','6','0','0','1','fruit');
