insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('宣克炅','true','【醉驾男子冲上上街沿】前晚21：30分许，民警在嘉定环城路巡逻时，发现一辆安徽牌照轿车开上上街沿，驾车男子烂醉如泥、趴在驾驶方向盘上。据了解，驾驶员Z某41岁、安徽来沪，其血液酒精浓度测试结果为291毫克每毫升，超醉驾标准3倍多。已被依法刑事拘留。所幸未造成人员伤亡。感谢网友图片。','2015-03-21 23:53','0','1','0','1','milk');
