insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国中医药出版社','true','《本草纲目》记载南瓜有补中益气之效，明《滇南本草》云“利小便”。现代医学研究指出，南瓜果实中有7%-17%的果胶，吸附力很强，能黏合与消除人体内的细菌及毒性物质，保护消化道黏膜，对消化道溃疡有疗效。南瓜果实中丰富的维生素有保护黏膜的作用，对于保护视力、预防眼疾、美容减肥均有一定作用。','2015-03-04 08:54','3','0','0','2','fruit');
