insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题295#【食疗驻颜法 好容颜吃出来】健脾消斑粥:山药30克,莲子,红小豆各15克,生薏苡仁,生芡实,白扁豆各10克,大枣10枚,加水煎沸40分钟后,加入粳米100克煮粥,早晚分服,连续1个月,可健脾去脂,并能消除脸上色斑。','2015-03-24 09:03','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题293#【食疗抑制宝宝口水－鲤鱼赤豆汤】材料：赤豆100克，鲜鲤鱼一条500克。做法：将赤豆煮烂取汤汁，将鲤鱼洗净去内脏，与赤豆汤汁同煮，放黄酒少许，用文火煮一小时。服用：取汤汁分三次喂服，空腹服，连服七日。','2015-03-24 09:01','0','1','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题291#【食疗小贴士】想要肌肤水润柔嫩除了外调还必须内养送上食疗小贴士：1牛奶能改善皮肤细胞活跃度增强皮肤张力、消除皱纹。2、大豆。抑制皮肤朽迈，防止色素沉着于皮肤。3、三文鱼。有效防止皱纹产生，与相宜本草敷食同源系列护肤理念不谋而合，如此好的食疗小秘籍，怎能不与闺蜜们分享？','2015-03-24 08:59','0','0','1','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题289#【食疗让女人更健康】1防治乳腺癌喝红酒；2、妇科肿瘤吃红皮果蔬；3、贫血多吃肝脏、瘦肉、菠菜，少喝浓茶、咖啡；4、乳腺病吃全麦食品和海带；5、经期不适喝加蜜热奶；6、雌激素低吃豆制品；7、吃蒜防治霉菌性阴 道炎;8、宫颈癌摄入叶酸;9、卵巢癌多吃高钙食品如虾米;10、痛经吃香蕉。','2015-03-24 08:54','1','1','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题288#【食疗去火办法】1.喉干嗓哑：饮淡盐水；服蜜梨膏；饮橘皮糖茶。2.咽喉肿痛：吃梨。3.口长水泡：切几片生姜入口咀嚼，可使水泡渐渐消弭。4.嘴唇干裂：黄瓜猕猴桃汁。5.头发枯槁：蜜枣核桃羹。6.其他去火办法：吃柚子，喝绿豆汤可去火。饮茶以铁观音和绿茶的效果最好。','2015-03-24 08:53','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题285#【食疗去黑眼圈2】苹果生鱼汤：把苹果、生姜、红枣洗干净后，切细放入瓦罐中，然后放入煎成微黄色的生鱼，用猛火煲滚，继而改用中火继续煲两个小时左右，分两次饮用，可以治疗气血不足和皮肤浮肿，防止黑眼圈和眼袋的出现，是一款比较美味的去黑眼圈小妙招哦，姐妹们不妨试一试。','2015-03-24 08:50','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题282#【食疗祛痘】痘痘肌肤的美眉们，一定要内服外敷，食疗方面呢推荐多吃含维生素A丰富的食物。因为维生素A能促进上皮细胞的增生，可调解皮肤汗腺，消除粉刺。含维生素A丰富的食物有金针菜、韭菜、胡萝卜、菠菜、牛奶、动物肝脏等。','2015-03-23 18:00','2','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题281#【食疗祛斑法】胡萝卜：维生素A可以让皮肤光洁白嫩，它还能够预防皮肤变粗糙和雀斑的产生。而胡萝卜就蕴含丰富的维生素A原，它在人体内可以转化成维生素A。我们可以通过多吃胡萝卜来达到祛斑的效果。','2015-03-23 17:59','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题279#【食疗美白】排毒类美白食物：想要肤色变得透亮健康，给身体排毒也是必不可少的措施，所以在日常多食用一些排毒食物也能实现美白的效果，这类食物主要有黄瓜、青木瓜和绿茶等，平时饮用绿茶，也可以把茶包放入浴缸中泡澡，都能起到排毒的功效。','2015-03-23 17:56','1','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题278#【食疗解决8种上火】①喉干嗓哑，饮淡盐水；②咽喉肿痛，吃生梨，用醋水漱口；③口角长泡，咀嚼生姜；④嘴唇干裂，喝黄瓜猕猴桃汁；⑤皮肤干痒，红薯炒嫩黄瓜；⑥头发干枯，吃蜜枣核桃羹；⑦咽喉干肿，吃蜂蜜梨膏；⑧鼻出血，吃水果西米露。','2015-03-23 17:55','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题276#【食疗分体质：手热的孩子多吃梨，手凉的孩子吃山药】 上火的表现如手心脚心总是热热的、嘴唇红红的、大便干燥，这样的宝宝多吃寒凉性的水果，如梨、猕猴桃。气虚的表现如面色黄、手凉、大便稀，多吃补气的食物吧，可以吃山药莲子粥、山药红枣粥等。','2015-03-23 17:54','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题274#【生活小妙招】电脑族护眼秘笈：1.揉捏耳垂：点按耳垂中点，女性可以佩戴带夹子的耳环，有空就多按按这个部位，既美丽，又缓解视疲劳。2.每天做眼保健操，然后转动眼球，或双手掌心搓热，热敷眼睛。3.眼疲劳人群食疗处方：喝菊花茶、决明子茶，多吃黄、绿叶蔬菜，常吃番薯。','2015-03-23 17:51','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题272#【去黑眼】利用食疗法去黑眼圈是个可行的方法，多吃蔬菜水果食物，每天坚持喝一杯红枣水不仅能帮助血气运行，同时还能减低因贫血而患黑眼圈的机会。此外，也可以通过和萝卜汁和番茄汁的方法，丰富的胡萝卜素和茄红素可以帮助眼部肌肤消除眼部疲劳，从而达到淡化黑眼圈的目的。','2015-03-23 17:48','4','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题271#【清肺润喉—白萝卜】白萝卜是中医食疗经常选用的，生吃效果好，榨汁效果更佳。而且其中含有的辣味成分可抑制细胞的异常分裂，进而预防癌症发作，萝卜还有杀菌、增进食欲和抑制血小板凝集等作用。萝卜中含有的大量膳食纤维和丰富的淀粉分解酶等消化酶，能够有效促进食物的消化和吸收。','2015-03-23 17:46','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('天寿永生','false','#健康话题270#【奇异果】含有丰富的维生素C、E、钾、叶酸等营养成分，是非常有益的保健水果，对于改善孕妇贫血也有着很不错的效果。奇异果也叫猕猴桃，种类繁多，但是却是一种药用和食疗价值极高的水果，猕猴桃可以鲜食、罐藏，又可以作为果汁、果酒、果脯、果酱等。','2015-03-23 17:45','4','1','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('时尚生活百科全集','false','热门 【如何降低肝癌风险】①注意饮用水安全。A.一些饮用水常被多氯联苯、氯仿等污染，B.蓝绿藻是致癌植物，C.华支睾吸虫感染可刺激胆管上皮增生。②扔掉霉变食物。尤其是霉变的玉米、花生，这些食物中含有黄曲霉毒素。③远离肥胖，避免糖尿病，肥胖和糖尿病是诱发肝癌的重要危险因素。','2015-03-15 00:24','1','0','0','7','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('瑶姐贝诗集团执行董事','false','咔咔寿99元/盒 酵素青梅做的几件大件事: 1、排毒/微笑：身体会越来越好； 2、清肠 /呲牙：肠道会越来越顺； 3、通便 /拥抱：宿便会越来越少； 4、祛痘 /可爱：脸蛋会越来越靓； 5、减脂 /亲亲：身材会越来越美； 6、调理 /抱拳：健康会越来越近； 7、解酒 /爱心：肝脏会越来越好；','2015-03-15 01:42','0','1','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师芦俊竹','false','#蔬菜熟吃优于生吃# 有人说生吃蔬菜可以利用其中的活性酶，其实不然，蔬菜熟吃我们总结了以下几个有点:1、加热可以病菌和虫卵2、烹调可以提高绿叶蔬菜和橙黄色蔬菜中维生素K和胡萝卜素的利用率2、烹调可以提高蔬菜中钙、镁的利用率。虽然生吃蔬菜，可以吸收更多的营养物质但是熟吃会更安全健康。','2015-03-20 18:15','7','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师芦俊竹','false','今天为小郊亭社区的叔叔阿姨们带来了膳食指南十条的讲座，叔叔阿姨们十分积极，还没开始就早早到了。互动的时候也很热情，讲座结束之后还有个阿姨拉着我问牛奶怎么喝 @中国网食品频道 @首都保健营养美食学会 @贝因美亲子 2高碑店','2015-01-23 16:39','0','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师芦俊竹','false','#食物颜色与营养#白色：豆腐，豆浆，牛奶，酸奶，奶酪——含有丰富的钙和蛋白质。每天需要喝1杯牛奶（250克）或者半块盒豆腐（125克）。黑色：木耳、香菇、海带、黑芝麻——含有大量微量元素和可溶性膳食纤维，具有抗衰老、除污染和预防慢性疾病的作用。每天吃10克干品即可达到保健效果。','2015-01-22 15:30','0','0','4','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('方格辉营养师','true','【为什么建议喝酒前吃点主食】 因为葡萄糖经糖醛途径生成的葡萄糖醛酸，是体内一种重要的结合解毒剂，在肝脏中能与许多有毒物质如细菌毒素、酒精、砷等结合，以消除或减轻这些物质的毒性或生物活性，从而起解毒作用。','2015-02-25 20:43','0','1','0','4','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('方格辉营养师','true','【老酸奶越稠越好吗】 不是，老酸奶是在普通酸奶的基础上，添加了凝胶剂或增稠剂，就成了“老酸奶”。食品添加剂在食品中添加的范围和计量是有严格规定的。如老酸奶的添加剂之一明胶，最大使用量为2.5 g/kg。越稠的老酸奶可能是添加剂超标，更要警惕非法添加物工业明胶。','2015-02-25 19:53','0','0','6','4','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 一年之计在于春，在万物复苏的春季，随着各种微生物的快速繁殖，过多的病毒或细菌乘虚而入，由于宝宝的抵抗力弱，很容易被病毒或细菌感染而生病。同时，春季的气候不仅干燥，而且变化无常，所以宝宝易患上呼吸道疾病。所以春季要注意对宝宝的各方面护理，同时进行合理的营养补充。','2015-03-12 11:20','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 鸡蛋的营养价值高，是很多宝宝的辅食必备，但是要注意烹饪鸡蛋不宜时间过长 时间过长会使蛋黄中的亚铁离子与蛋白中的硫离子化合生成硫化亚铁，很难被吸收；煎鸡蛋过老，鸡蛋清所含的高分子蛋白质会变成低分子氨基酸，这种氨基酸在高温下可能形成对人体健康不利的化学物质哦。','2015-03-04 16:59','0','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 过年时，家庭聚会中小朋友在一起，好吃的零食自然少不了。推荐一款自制零食：燕麦曲奇饼。自家做的小零食，既能保证宝宝在主餐之外科学合理地补充营养，又能避免市售小零食因为添加剂过多带来的健康隐患。赶快给宝宝准备一份属于自己的“妈妈”牌小零嘴吧！','2015-02-25 16:19','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 给宝宝转奶应注意：1、转奶时间把握：要在宝宝身体完全健康的状态下进行，不要在换牙、感冒或打免疫苗7天内转奶。2、转奶要循序渐进，新旧奶粉慢慢转换，不可操之过急，整个转奶的过程要历经一到两周的时间。3、避免让宝宝在早上空腹的第一餐或晚上临睡觉前最后一餐转奶。','2015-02-14 10:34','0','0','2','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉#一般来说，小宝宝在4到10个月之间会出乳牙，家长可以观察宝宝出乳牙的情况，它是衡量宝宝健康的一个标准。宝宝乳牙成长时期因人而异，宝妈不要操之过急，只要在十个月内长出乳牙的大多数是没有问题的。但如果宝宝一岁了还没有长牙，那就需要去检查下，可能缺钙、维生素D补充不足等。','2015-01-29 10:36','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 一岁后，宝宝基本上没有必要在夜里吃奶了，吃夜奶，不但影响宝宝和妈妈的睡眠，也对宝宝的牙齿不利，还会影响到宝宝的消化功能，因此要慢慢的把宝宝吃夜奶的习惯调整过来。根据宝宝的适应情况，一般调整一个星期左右就可以了，养成习惯后，还可以提高宝宝的睡眠质量哦。','2015-01-28 14:50','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉#想家里绿意盈盈增添生气，让宝宝生活得舒适健康，适当绿化是不错的选择。绿化可降低室内二氧化碳浓度，可在一定程度上减少细菌数，还可明显改善视力，提高人的反应能力。要提醒的是，绿化前要了解植物特点，如丁香、夜来香、夹竹桃、洋绣球、郁金香、松柏类花木等不宜在室内种植。','2015-01-27 10:37','0','0','1','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 宝宝辅食拒绝“重口味”：1.高盐饮食可使宝宝口腔唾液分泌减少，会令细菌、病毒在呼吸道存在，从而致病。2.高盐饮食会影响宝宝体内对锌的吸收，导致缺锌。3.高盐饮食会加重宝宝的心脏、肾脏负担。4.宝宝若从小养成重盐的饮食习惯，以后不易纠正，重盐饮食习惯容易引起高血压等疾病。','2015-01-13 10:28','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 1-3岁是宝宝语言能力发展迅速的时期。对1-3岁孩子进行语言培养可以利用趣味性的活动，或者将其渗透于日常生活的各个环节，如有趣的游戏、观察、散步等活动中，使语言和具体事物结合起来。成人要注意发音标准，用词准确，说话完整、简洁，尽量少说方言、土语、半截子话。','2015-01-12 11:05','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 在有的家长看来，儿童“涂鸦”似乎没什么价值，也不明白他们要表达什么，所以会阻止宝宝乱涂乱画，其实“涂鸦”客观上对宝宝手眼脑的协调配合、增强大脑对手的指挥能力有着积极的作用。所以家长应该给宝宝创造涂鸦的环境，为他们准备安全的纸笔等工具，保护并鼓励他们的想象力。','2015-01-02 11:18','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 糖果是小孩子无法抵御的诱惑，不过甜食摄取过多，会导致注意力和情绪以及身体健康上的问题，于是很多家长禁止孩子吃糖，但宝宝很爱吃糖怎么办？家长不妨跟宝宝做个约定，规定每周可以吃的量及吃的时间，比如每天下午可以吃一颗糖。一味的禁止和阻止，会让宝宝的情绪受到伤害哦。','2014-12-31 10:28','0','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('德国Natamil婴儿奶粉','false','#德国Natamil奶粉# 宝宝不知为何拉肚子？可用蒸熟的苹果缓解。苹果中含果胶，蒸过的果胶具有吸收细菌和毒素的作用，还有收敛止泻功效。苹果洗净后，带皮切成小片（近果皮处的果胶含量更丰富，所以要带皮），放入小碗中，隔水蒸5分钟即可，稍稍冷却后，用勺捣成泥状即可食用，平均每天吃2-3次。','2014-12-25 14:28','3','0','1','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('上海自贸区红酒交易中心','true','#葡萄酒健康# 据德国《科学概揽》报道，英国科学家发现为什么法国红葡萄酒能不象别的酒精那样会引发心血管病，原因是红葡萄皮中含有多酚物质，此物质可防血管变窄。但一般血压过高的病人要慎重饮用，因为大部分冠心病人血压不太高，是比较合适的。','2015-03-20 09:35','1','3','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('上海自贸区红酒交易中心','true','#酒庄学堂#自1989年以来，玫瑰庄园(Château Montrose)已经成为圣爱斯泰夫最稳定的列级名庄，最近一些年份的酒质量都非常稳定。如果修改当年的分级，玫瑰酒庄完全有能力得到梅多克地区一级庄园的头衔。多年以来，玫瑰酒庄的酒一直以结构宏大、醇厚、强劲有力著称，被称为“圣埃斯泰夫的拉图酒庄酒”。','2015-03-20 09:22','0','7','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('上海自贸区红酒交易中心','true','#葡萄酒知识#在评价一款葡萄酒的品质时，最高的标准就是复杂性了。当你打开一瓶葡萄酒，每隔一段时间葡萄酒都会呈现出不同的香气和口感，这就是葡萄酒的复杂性。伟大的葡萄酒并不一定酒力强劲，但一定是复杂的，香气和口感不断发展变化，永无止境。','2015-03-19 15:35','0','7','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('上海自贸区红酒交易中心','true','#葡萄酒健康#波士顿塔夫茨大学在最新一期《美国临床营养学杂志》上报告说，为研究饮酒对人体骨骼的影响，他们选择了1182名男性和1500多名女性作为研究对象，其年龄在29岁至86岁之间。 研究发现，每天饮用一至两杯葡萄酒者的骨密度大于不饮酒者；而每天饮用两杯以上烈性酒者的骨密度低于不饮酒者。','2015-03-18 13:22','0','2','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('上海自贸区红酒交易中心','true','#葡萄酒健康#美国德州农工大学医学院健康科学研究中心研究发现，在红葡萄和花生中存在的一种名为白藜芦醇（Resveratrol）的物质可帮助预防衰老带来的记忆衰退。谢蒂博士和他的研究团队发现，白藜芦醇能作用于大脑中的海马体。白藜芦醇对海马体的作用，则可能对防止记忆衰退有积极效果。','2015-03-18 10:12','0','1','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('上海自贸区红酒交易中心','true','#饮酒健康#【美酒配咖啡，这样真的好吗？】酒精伤身，咖啡因具有兴奋、提神和健胃的作用，过量亦可造成中毒。如果酒精与咖啡同饮，犹如火上浇油，可加重对大脑的伤害，并刺激血管扩张，加快血液循环，增加心血管负担，造成的危害超过单纯喝酒的许多倍，甚至更容易危及生命。','2015-03-13 10:24','0','2','0','1','milk');
