insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('里奥哈优质葡萄酒原产地','false','#求是Rioja# 应酬客户在所难免，商务宴请中的选酒可是一门学问：①酒要与菜品口味相搭配，客户吃得开心能促进合作；②选酒精度低的酒，尤其于常应酬的你而言身体健康不容忽视；③选酒有特色即可，忌选名酒引发拼酒事件；④如果你记不住以上几点，小编觉得选里奥哈的葡萄酒就万无一失啦！','2015-03-17 15:30','0','7','0','1','wine');
