insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('小猪食册','false','热门 #零基础，在家轻松学做面包# 柏翠PE6800【培根珍蟹三明治】为平凡早餐添趣味,自从有了柏翠PE6800, 晚上吃饭时启动自动做好吐司,不折腾隔天或搭配牛奶或做个三明治 简单早餐自己动手健康卫生说不出的满足感 !早餐还在外面吃？你OUT啦~ 详见图8 9~ @柏翠电器官方微博','2015-03-11 11:33','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('厦门美食','true','#微资讯# 【 吃糖危害比吸烟还大！】糖摄入多了，会有饱腹感，不仅带来胆固醇紊乱、癌症、心脏病、糖尿病和脂肪肝等症状，甚至会损害记忆，导致早亡。据统计，长期高糖饮食者寿命短10~20年。生活中，很多菜品、水果含糖量远高于想象，要远离这些糖不是容易事。快告诉小伙伴怎么吃糖最健康！戳图↓','2015-03-23 16:40','1','0','0','1','fruit');
