insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('旅游卫视官方微博','true','【8招助你睡个好觉】①晚餐清淡，可吃些小米、大枣或莲子；②卧室不放太多绿植，不装修成红色；③床头位置尽量朝北，枕头与肩同高；④每天晒一小时太阳；⑤睡前8小时别喝咖啡；⑥每晚泡脚10分钟；⑦睡前练习腹式呼吸；⑧睡不着可闻闻洋葱或生姜。最后提醒，11点前睡觉最健康！该准备准备睡啦～','2015-03-21 22:00','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('李怡Candice','true','【最近精油总是躺着中枪】台湾一位男生因打翻放在茶几的薰香精油，在清理时被意外点燃的“精油”烧伤！原来这瓶玫瑰薰香精油是混了酒精与香精的“精油”（市面上常见）。记者也做了比较：天然的纯精油无法点燃，而含有酒精的薰香精油一点即着火。为了自身及家人的安全请不要再使用劣质精油！','2014-08-27 18:31','0','2','0','2','wine');
