insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('爱尚女558','false','【护肤小窍门】草莓酸奶祛痘：将草莓去蒂洗净榨汁，加入酸奶喝面粉混合均匀，敷于脸部20分钟后用清水洗净。草莓具有消炎和收敛肌肤的作用，混合酸奶能改善肌肤油脂的分泌，能有效缓解痘痘的生长。','2014-10-11 07:19','3','0','3','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('爱尚女558','false','【柠檬茶换肤面膜】材料：柠檬草精油2滴，佛手柑精油1汤匙，绿豆粉1汤匙，纯净水；制作：将绿豆粉导入碗里，加纯净水和甘油搅拌，再滴入柠檬草精油和佛手柑精油调和成糊状。使用：洁面后，将面膜敷于脸部15分钟后用温水洗。功效：嫩肤洋洋，促进血液循环。','2014-10-07 08:14','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤达人-小卓老师','false','癌细胞最喜欢吃的就是乳酸糖，知道乳酸糖是什么吗？就是脂肪的代谢物，去脂已经不是悦不悦己，悦不悦别人的问题！关乎着你罹患重大疾病的风险高于有肌肉的人！ 2 全福路','2015-03-01 21:17','0','0','2','1','milk');
