insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('糖必应','true','#饮食风向标#【糖尿病人的饮食切忌“过精”】糖友可以多吃点粗粮，但千万不要“粗粮细做”。粗粮磨得太细，也就成了细粮。以主要含淀粉的玉米为例，如果磨得太细，在消化过程中很快就转化为葡萄糖，容易引起血糖升高，丧失了粗粮本来的保健作用。详细>> O网页链接','2015-03-19 18:22','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('糖尿病教育新7点','false','#健康指南#【糖尿病饮食】蔬菜中含水分多，能量低，富含植物化学物质，是提供微量营养素、膳食纤维和天然抗氧化物的重要来源。每天蔬菜摄入量要达到300-500克，最好深绿色蔬菜占一半。点击了解详情。 O网页链接','2015-03-11 18:29','3','0','0','1','fruit');
