insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('免疫力抗癌','false','#抗癌防癌# 【抗癌粥】椰果丁、木瓜、玉米粒、牛奶各适量，燕麦片40g，白糖3g。燕麦片泡发洗净，木瓜去皮去子切丁；锅置火上，倒入清水，放入燕麦片，以大火煮开；加入椰果、木瓜、玉米粒、牛奶同煮至浓稠状，调入白糖拌匀。在燕麦中加入牛奶既改善口味，又补充了蛋白质。具润肠通便、排毒防癌之用。','2015-03-19 11:01','0','0','3','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('免疫力抗癌','false','#健康养生# 【西红柿炒鸡蛋里隐藏的健康秘密】1、西红柿含有丰富的胡萝卜素；2、番茄红素具有特别的抗氧化能力；3、鸡蛋是营养丰富的食物；4鸡蛋中含有丰富的DHA和卵磷脂等；5、鸡蛋炒西红柿是营养素互补得很不错实例。','2015-03-18 17:00','4','0','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('免疫力抗癌','false','#健康养生# 【常吃四种食物有助增强体质】1、花菜：又名菜花、椰菜花，含大量抗癌酶，是延年益寿食物中的首选食物。2、草莓：可以改善肤质，减轻腹泻，缓解肝脏及尿道疾病。3、大豆：含有大量的植物雌激素，对女性的健康极其重要。4、红薯：富含大量纤维、钾、铁和维生素B6，能防止衰老，预防动脉硬化。','2015-03-16 17:01','2','0','0','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('免疫力抗癌','false','#抗癌防癌#【梨的抗癌功效】梨能生津、润燥、清热、化痰，古代医家多用之于食道癌、贲门癌和胃癌。由于梨里所含的胡萝卜素、维生素B2、维生素C等都具有一定的防癌抗癌作用，所以梨适宜于鼻咽癌、喉癌、肺癌患者服食。','2015-03-16 11:01','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('免疫力抗癌','false','#抗癌防癌#蔬菜汁是一种健康的饮品，富含粗纤维素：①胡萝卜汁含有丰富的β胡萝卜素,能提高人的食欲和对病菌感染的抵抗力。②芹菜汁，增强人的食欲，对排除体内毒素。③白菜汁，含有丰富的维生素C。④黄瓜汁在强健心脏和血管方面甚有功效，还能调节血压，增强人的记忆力。','2015-03-15 11:00','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('免疫力抗癌','false','#健康养生#【女人养肾必备四种水果】①葡萄不仅含有矿物质钙、钾、磷、铁和多种维生素，还含有多种人体必需氨基酸。②桑葚具有补血滋阴、生津润燥和补肝益肾作用。③黑加仑具有降低血脂、养肾、抗氧化和提高免疫力等作用。④黑枣有补益脾胃，养肾阴血的功效。','2015-03-14 17:01','2','0','0','2','wine');
