insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容讲师阿文','true','【美容DIY】草莓50克，鲜奶1杯。将草莓捣碎，用双层纱布过滤，将汁液混入鲜奶。拌均匀后，将草莓奶液涂于皮肤上加以按摩。保留奶液于皮肤上15分钟，以清水洗净。功效：既能滋润、清洁皮肤，更具温和的收敛作用及防皱功能。纯天然，最给力！','2015-03-12 08:28','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤老师Joe','true','【10种食物美容抗衰老】①樱桃能帮助面部皮肤嫩白去皱清斑；②石榴有很强抗氧化作用；③番茄延缓衰老减少色素沉淀；④柚子使皮肤光滑细腻；⑤黄豆调理雌激素增强皮肤活力；⑥葡萄是抗老保湿佳品；⑦菌类调节免疫系统；⑧燕麦减少脂肪；⑨黄瓜对抗皮肤老化减少皱纹；⑩柠檬可促进肌肤代谢抑制色素沉淀。','2015-03-14 00:45','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤老师Joe','true','【啤酒洗脸】啤酒中含有多种ORGNITU维生素和矿物质，对改善肌肉功能、健全神经系统等均有好处，对皮肤有着良好的滋养补水作用。此外，啤酒中含有维生素B和锌，可有效增强身体的免疫力，改善皮肤状况，减缓衰老。','2015-03-11 16:44','0','3','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师阿文','true','【缩小毛孔小贴士】1、用水果敷脸：西瓜皮、柠檬皮等都可以用来敷脸，它们有很好的收敛柔软毛细孔、抑制油脂分泌及美白等多重功效。2、栗皮紧肤：取栗子的内果皮，捣成末状，与蜂蜜均匀搅拌，涂于面部，能使脸部光洁、富有弹性。','2015-03-16 15:41','3','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师阿文','true','【肉毒素瘦小腿对日常活动影响不大】小腿后侧肌肉群主要是浅层的腓肠肌与深层的比目鱼肌构成。走路的功能主要是依靠较深层的比目鱼肌，而萝卜腿往往是因为浅层的腓肠肌太过粗壮所造成，所以阻断支配腓肠肌的神经可以安全又有效的改善小腿外形，对于日常生活并不会造成太大的影响。','2015-03-15 07:40','1','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师阿文','true','【吃得健康 肥肉跑光】1.均衡饮食：均衡的摄取六大类食物（五谷、肉蛋类、奶、蔬菜、水果及油脂）；2.少油少糖：采用蒸、煮、凉拌方式，少吃含精食物；3.高纤 维：选择高纤维食物增加饱足感；4. 饮酒节制：喝酒会增加热量；5.细嚼慢咽：防止吃进多余热量。','2015-03-12 07:40','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容老师George','true','【减肥】过度的肥胖，会影响你的身体健康。出现肥胖问题，一定要及时减肥瘦身才行。制定一周减肥计划，让你用饮食来吃出苗条。周一：喝番茄汤，选择喝番茄汤的原理主要在于清肠的作用；周二：适当饮食；周三：优格乳减肥法 健身操；周四：适当饮食 散步；周五：牛筋牛蒡汤 慢跑。','2015-03-09 12:36','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容老师George','true','【注意饮食】红血丝皮肤的MM饮食上可多摄入一些牛奶、豆制品及新鲜的蔬菜、水果，以增强皮肤抵抗力。注意增强血管壁韧性和弹性，改善微循环，要从根本上来缓解和改善皮肤泛红和红血丝问题。鱼、虾、蟹等极易引起皮肤过敏，要尽量避免食用。','2015-03-07 08:37','2','0','1','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容老师George','true','【教你护肤：对抗咖啡色黑眼圈】 上妆或卸妆时绝对不可揉搓眼睛，尽量避免刺激肌肤，同时在做眼部集中美白护理时排除已经沉着的色素。 摄取维他命C和E：蔬菜、糙米、薏仁茶既有美白效果，又可帮助机体抵抗紫外线。尽量少在白天摄取柠檬、芹菜等感光性高的蔬果。','2015-03-05 00:36','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('首席护肤讲师阿文','true','【防晒修复】橄榄油可防止紫外线对皮肤的伤害，避免晒红、蜕皮，其功效和防晒霜相差无几。晒后将特级超纯橄榄油涂在皮肤上，可以修复晒伤的皮肤，还能预防皮肤癌的发生。Tips：每天40克番茄加10克食用橄榄油，做成凉菜吃，可以将被晒伤的可能性降低40%。','2015-03-07 04:55','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家-小旭老师','true','【DIY面膜】材料：红色胡萝卜、苹果。做法：用榨汁机把胡萝卜和苹果的汁液榨出，搅匀后涂在脸上，20分钟左右用温水洗净。功效：苹果可以养颜除斑，使肌肤润泽有弹性，也有祛斑作用，可防治皮肤粗糙及雀斑。','2015-01-14 23:03','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师Live','true','【牛奶+食盐=告别粗糙】材料：牛奶、食盐做法：在牛奶中混入少量的食盐，可以有效调理肌肤，改善粗糙暗沉，去掉废旧角质，让肌肤变得光滑细腻。首先准备好一个容器，在一起倒入少量的食盐和清水，待其溶化后，再加入4勺脱脂奶粉，混合搅拌均匀成糊状，将其涂抹在脸上20分钟左右洗净，便可以告别粗糙。','2015-03-08 00:01','0','0','4','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师Live','true','【牛奶+草莓=清洁面膜】材料：牛奶，草莓做法：准备好50g草莓洗净后放入榨汁机中榨成泥状，并在其中加上一小杯纯牛奶调和，拌匀后取草莓奶液，涂于面部及颈部加以按摩，约15分钟后清洗掉便可。具有很不错的清洁作用，能清理毛孔内的垃圾、油垢，改善油性肤质。','2015-03-07 21:51','4','0','3','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('首席护肤讲师rock','true','【美容护肤必知小常识】看来许多美眉们都有皮肤过敏的困扰呢，那么我们在饮食上要多食新鲜的水果，蔬菜，饮食要均衡，最好包括大量含丰富维他命C的生果蔬菜，任何含维他命B的食物。饮用大量清水，除了各种好处外，它更能在体内滋润皮肤。平时自制一些营养面膜，如黄瓜汁面膜，丝瓜汁面膜等逐步改善肌。','2015-03-16 06:09','4','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级营养师殳文','false','夏季腹泻的实用偏方:夏季腹泻可喝苹果煮水,有利尿止泻的作用.腹泻感染康复期时会感到无胃口,这是一种身体保护的机制,可到市场买陈年萝卜干煮水,有消食导滞的作用,可以缓解这一症状.老是腹泻不好的,还可尝试用冷水煮茶（最好用半发酵的乌龙茶、铁观音）放进冷水里,茶叶和水一起煮开便可,有止泻的作用。','2013-06-26 21:51','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级营养师殳文','false','#养生【吃哪补哪 补身体最需要的食物】俗话说：吃什么补什么。现给你提供一张全身的“食物地图”，告诉你从头到脚的饮食秘方，让你吃得美丽，吃得健康。护脑：菠菜 护眼：红薯 护发：海带 护肺：番茄 护腿：香蕉 护心：深海鱼 护肾：黑豆 护胃：甘蓝 护甲：鸡蛋 护肤：西蓝花。 @高级营养师殳荣','2013-06-16 21:16','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级营养师殳文','false','#脯乳期妇女膳食指南# 忌烟酒，避免喝浓茶和咖啡：乳母吸烟（包括间接吸烟）、饮酒对婴儿健康有害，喝浓茶、咖啡也可能通过乳汁影响婴儿的健康。因此，为了婴儿的健康，脯乳期应继续忌烟酒、避免饮用浓茶和咖啡。 @高级营养师殳荣','2013-06-08 22:37','0','1','1','4','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级营养师殳文','false','#脯乳期妇女膳食指南# 产褥期要重视蔬菜、水果摄入：“坐月子”不吃蔬菜和水果的习俗是很不利于健康的。新鲜蔬菜和水果含有多种维生素、矿物质、膳食纤维、果胶、有机酸等成分，可增进食欲，增加肠蠕动，防止便秘，促进乳汁分泌，是产妇不可缺少的食物。因此产褥期要重视蔬菜水果摄入。 @高级营养师殳荣','2013-06-08 22:36','8','0','1','1','fruit');
