insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('苏泊尔炊具','true','#居家巧思# 【如何健康吃方便面】①先冲洗：先用温水洗净搅拌，倒出含有蜡涂层的水。②应煮着吃：煮面更易吸收水分，利于肠胃消化，同时加上鸡蛋与蔬菜，使面条更加营养。③吃完面把汤倒掉：调味料不要全加进去，避免盐分过多。④吃完最好吃些水果。⑤最重要的是不要长期吃。（via：网络）','2015-01-29 10:10','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('苏泊尔炊具','true','#居家巧思# 【5种食物，最“懂”女人心】1、酸奶：润肠补钙。酸奶可以改善肠道环境，提高机体免疫力。2、豆类：天然荷尔蒙。有助调节女性荷尔蒙以及减轻女性更年期的症状。3、燕麦：安神止痛。能稳定情绪。4、红肉：补铁高手。5、西红柿：抗衰抗感染。对抗击乳腺癌有帮助。（via：网络）','2015-01-28 10:04','1','0','2','1','milk');
