insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('家庭健康首席记者马灵慧','true','五种日常食物堪比伟哥：1、大蒜：大蒜可以增加一氧化氮生成，还可以帮助调节血脂浓度，降低血脂，恢复健康。2、西红柿：西红柿是预防和降低男性患前列腺疾病最好的食物。3、牡蛎：有利于精子生成。4、韭菜：温肾补虚。5、枸杞子：滋补肝肾、益精明目、养血。','2014-10-17 13:11','3','0','0','1','fruit');
