insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('冬妮i宝贝','true','【婴儿肠绞痛怎么办】妈妈要警惕宝宝肠绞痛，一般开始于二至四周大的小婴儿，常见症状：哭闹不安、脸部胀红、膝盖缩起、握拳踼脚。如何改善呢：1、喂奶时不要让宝宝吸入过多空气。2、可频繁地拍背排气。3、试着少量多餐喂奶，牛奶温度应适中。4、轻柔按摩宝宝的腹部。5、帮宝宝洗个温水澡。','2015-03-06 14:03','0','0','1','1','milk');
