insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级护肤讲师Deram','true','【5个你可能不知道的长痘原因】1、牛奶或乳制品可能会使一些人增加皮脂分泌，因而堵塞毛孔；2、摄取过多的碳水化合物，使胰岛素增加，而引起皮肤发炎；3、没有足够的脂肪酸来减少皮脂分泌；4、压力下产生的类固醇激素可加重痤疮；5、阳光下暴晒，也可能会增加皮肤的油脂生产哦。','2015-01-26 03:11','0','0','2','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级护肤讲师Deram','true','【柠檬汁祛痘】专家说法：柠檬汁含有果酸，可以加强代谢软化角质，排除污垢，具有很强的清洁能力。但它果酸浓度较高，会有过敏的副作用。再者，柠檬是感光植物，白天使用维生素C，它的美白作用也会遭到紫外线的影响。敏感肌肤不要轻易尝试，不够安全。','2015-01-25 14:12','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级护肤讲师Deram','true','【皮肤最喜欢的食物】1、大豆：其中含有丰富的维生素E，不仅能破坏自由基的化学活性、抑制皮肤衰老，还能防止色素沉着。2、牛奶：它是皮肤在晚上最喜爱的食物，能改善皮肤细胞活性，有延缓皮肤衰老、增强皮肤张力、消除小皱纹等功效。3、猕猴桃：富含维生素C，可干扰黑色素生成，并有助消除皮肤上雀斑','2015-01-24 10:02','1','0','1','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级护肤讲师Deram','true','【DIY番茄蛋白收毛孔面膜】材料：番茄2个、蛋白1只。做法：将茄肉挖出并捣烂成茄蓉后，加入蛋白拌匀，敷面10分钟后清洗。功效：番茄的酸性汁液有助平衡肌肤的pH值，能改善过盛的油脂分泌，清除表皮老化角质污垢，而蛋白有助收细孔。','2015-01-23 18:51','3','0','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美美滴护肤John','true','【护肤小知识】1.绿茶（多酚抑制黑眼圈）。2.芝麻（富含对眼球和眼肌具有滋养作用的维生素E）；3.胡萝卜（维生素A维持上皮组织正常机能，改善黑眼圈）；4.海带（富含铁质，增强血红蛋白输送氧分和营养成分的能力）；5.鸡蛋（优质蛋白质缓解黑眼圈）。','2015-03-06 16:42','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美美滴护肤John','true','【蚊虫叮咬，涂点薄荷】蚊虫叮咬引起的发痒，清凉油、风油精可治，但含酒精成分，不适合皮肤敏感者；激素类药物，效果好却不适合孩子。其实，不妨试试薄荷。薄荷是常用中药之一，有清香升散功效，外用可治神经痛、皮肤瘙痒、皮疹和湿疹等。蚊虫叮咬后可将薄荷叶捣碎敷于患处，有很好的止痒效果。','2015-03-05 15:42','0','1','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美美滴护肤John','true','【混合性肌肤保湿面膜】准备蜂蜜1匙，新鲜胡萝卜汁1匙，优酪乳75毫升就可以了，做法也并不复杂，只要将蜂蜜、胡萝卜汁、优酪乳混合调匀，然后均匀涂在脸上，待20分钟后洗净便可。这样的主要功效就是有效促进肌肤的油水平衡，同时滋润干燥的肌肤，令皮肤水嫩透白且有光泽。','2015-03-04 04:41','2','0','0','1','milk');
