insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤老师小鱼a7','false','【让人越喝越年轻的自酿青梅酒】青梅酒具有三大功效：1.能调节肠胃；2.排毒；3.防老抗衰。做法也超简单，很容易学哦：1 500克青梅洗干净；&nbsp; 2 焯青梅；3 沥干水分的青梅装入瓶中，分两次倒入200g冰糖，然后倒入。','2015-03-24 09:47','0','2','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤老师小鱼a7','false','【蜂蜜柠檬水】新鲜柠檬切片(每次三至四片即可)，注入凉开水，调入蜂蜜，放入冰箱冷藏至少两小时即可。蜂蜜柠檬水每天喝对女性护肤的作用非同小可，蜂蜜柠檬水除了能保养皮肤之外，还能促进细胞再生、改善睡眠状态哦，因此儿童和压力大的上班族都能喝！','2015-03-24 09:17','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤老师小鱼a7','false','【香蕉牛奶护肤】方法：准备一根熟透的香蕉和适量的牛奶；将香蕉去皮捣碎成泥，加入少许牛奶一起混匀；清洗干净面部肌肤，将香蕉牛奶泥均匀涂于面部肌肤，保持15分钟的时间，再用温水洗净即可。功效：此方法不但能美白嫩肤，同时还能细化皱纹，改善暗沉粗糙肤色哦！','2015-03-23 22:46','4','0','4','2','milk');
