insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级内调护肤讲师天天','false','【珍珠粉蜂蜜美白面膜材料】蜂蜜1小勺、蛋白一个、珍珠粉适量、柠檬精油1滴做法：将以上材料调配好搅拌均匀成粘稠状，脸部清洁洗净后，将其均匀的敷在面部，静静等上15-20分钟，用温水将脸清洗干净，每周1次。功效：珍珠粉和柠檬都具有美白亮肤、净化皮肤的功效，配上蜂蜜，还可润肤保湿。','2015-02-27 18:42','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤咨询-大鹏','true','【雾霾天健康提醒】雾霾又来了 大家注意了：①尽量少开窗。②外出戴口罩。③饮食清淡多喝水，多吃新鲜蔬菜和水果，如梨和枇杷等，这样不仅可以补充维生素，还能润肺除燥。④雾霾天不宜晨练。⑤有哮喘、慢性阻塞性肺病的患者，应规律服药，防止病情加重。','2015-03-11 13:53','3','0','0','1','fruit');
