insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('W美容护肤讲师-天明','true','【女性养生的水果】1.西柚：含有天然叶酸。2.苹果：含有多种维生素和矿物质、苹果酸鞣酸和细纤维等，许多孕妇怕胖，多吃苹果则可防止过度肥胖，同时对胎儿发育很有帮助。3.樱桃：所有水果中，樱桃所含的铁质特别丰富，孕妇常吃樱桃，生下的宝宝多拥有一张红润的健康面容。','2015-03-18 09:31','4','0','0','1','fruit');
