insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤老师芹子','true','【护肤---红酒美容法】酿酒红酒的葡萄果中含有大量的抗氧化剂。其中的SOD能中和身体所产生的自由基，令肌肤恢恢复美白亮泽的健康状态。而葡萄籽富含的营养物质“多酚”，其抗衰老的能力是维生素E的50倍。红酒中低浓度的“果酸”还有抗敏、洁肤的美容作用。','2015-02-17 10:26','2','3','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤老师芹子','true','【苹果吸油面膜】材料：苹果两个、化妆棉适量。做法：将冰冻了的苹果榨汁，沾湿化妆棉敷在油脂分泌旺盛的部位如T字位、鼻翼、下巴，10分钟后清洗。美容功效：苹果中的果酸成分能吸走面上多余的油脂。','2015-02-16 14:27','4','0','0','1','fruit');
