insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('耗小妖铿','false','【苹果和梨别吃子】啬薇科植物种子如杏仁、苹果子、梨子等含苦杏仁苷，食用后在胃肠内酸和酶作用下，水解产生氰氢酸使人中毒。苦杏仁苷在木薯中含量达万分之二，九成集中于薯皮，因而木薯食用要去皮。苹果或梨较安全，但应去核，别吃子。','2015-01-08 17:49','6','0','0','1','fruit');
