insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('晓爱健康养生','true','【放盐的讲究】1.豆油、菜籽油做菜，为减少蔬菜中维他命的损失，应炒过菜后再放盐；2.用花生油做菜，应先放盐炸锅，减少黄曲霉菌毒素；3.用荤油做菜，可先放一半盐，去除荤油中有机氯农药的残留量，而后再加入另一半；4.做肉类菜肴时，为使肉类炒得嫩，在炒至八成熟时放盐最好。','2015-03-11 20:37','1','0','0','2','milk');
