insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('昆仑山雪山矿泉水','true','#雪山矿泉水物语#现在的年轻一族口渴时喜欢选择果汁、饮料，不经常喝水，这是不健康的习惯。虽然果汁、饮料当中含有水，但还有能量和其它物质，不能完全替代水的作用。因此，小昆建议选择昆仑山雪山矿泉水，它属于小分子团水，极易被人体吸收，饮用它可以帮你迅速补充水分，充满青春活力！','2015-03-17 13:30','2','0','0','1','fruit');
