insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容--小C','true','【夏日吃玉米抗衰老哦】玉米的功效1、健脾益胃2、抗衰老3、防止便秘，防止动脉硬化4、防癌5、美肤护肤功效6、降糖功效7、通便功效8、预防肿瘤功效9、利胆、止血功效。食用玉米禁忌：1、变质玉米可致癌2、添加杂粮。','2015-01-15 13:20','4','0','0','8','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容--小C','true','【火锅的健康吃法：先菜后肉】火锅怎么吃？有人吃火锅先肉后菜，有人吃火锅爱喝冷饮，有人爱喝涮锅后的锅底汤…营养专家称，这些都是错误的吃法。先肉后菜，蔬菜会吸收残留锅内的嘌呤等有害物。正确的顺序应是先土豆类、后叶菜类，最后才是肉类。肉一定要煮熟，不要冷热交替。','2015-01-14 22:19','1','0','0','3','fruit');
