insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('顶级护肤营养讲师蓉蓉','true','【4类美容王牌食物】1.蘑菇：女人驻颜王牌，富含硒可促进皮肤新陈代谢和抗衰老，预防皱纹；2.大豆：内含异黄酮是植物性激素，美容效果好。3.酸奶含有大量活性乳酸菌，能抗衰老，对脾胃虚弱的女生更能帮助消化。4.薏米是女人贴心“闺蜜”，消除水肿的功效一流，且清血解毒，对斑点和青春期的痘痘有效。','2015-01-31 13:40','2','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('顶级护肤营养讲师蓉蓉','true','【蔬果蛋黄面膜】胡萝卜、苹果、黄瓜都含有丰富的维生素和微量元素，是滋养皮肤的面膜材料，蛋黄是一种非常廉价而优秀的天然美容材料，配合滋润度一流的蜂蜜使用，具有非常好的润肤抗皱功效。','2015-01-30 13:00','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('顶级护肤营养讲师蓉蓉','true','【水果美容】①草莓：增白滋润保湿；②西红柿：平衡皮肤的ph值，祛斑美白；③樱桃：促进角质层的形成；④菠萝：淡化面部色斑；⑤苹果：淡化面部雀斑及黄褐斑；⑥柠檬：美白抗老化；⑦葡萄：抗氧化；⑧猕猴桃：消除雀斑和暗疮；⑨桔子：增强肌肤弹性平抚面部小细纹；⑩桃子：预防色素沉着和皱纹。','2015-01-28 11:41','7','0','0','1','fruit');
