insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('佳美氏3零玉米油','true','#羊羊得意#特色烤羊排属于清真菜，主料为羊排，辅料为洋葱，鸡蛋，小麦面粉，将腌好的羊排放入烤箱中烤制，用不了多长时间，外焦里嫩，肉烂味香的烤羊排就出炉了，因羊肉胆固醇含量较低，同时具有补血、补虚养身、补阳等功效，所以老人小孩也可适量食用。 O网页链接','2015-03-05 11:00','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('滴露官方微博','true','#洗手享美食# 【喝豆浆的注意事项】①原料要选用上等的非转基因大豆(黄豆)，可以加红豆、绿豆等，增加营养。②大豆一般要浸泡10余小时。气温较高时，应放入冰箱或多换水，避免菌类滋生。③豆浆必须要烧开喝，不要往豆浆里打鸡蛋影响加热和营养。④少吃糖，建议调入蜂蜜。⑤建议早餐豆浆牛奶换着喝。','2015-03-21 11:32','2','0','1','1','fruit');
