insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美国ARIIX爱睿希健康管理','false','【这10种最差零食一定要少吃！】健康时报邀请北京、上海、天津、大连、郑州、哈尔滨等地八位三甲医院营养科主任选出最差零食和健康零食：①最差零食前三甲：薯片/薯条、汽水等碳酸饮料、蛋黄派。②越是香酥可口越要少吃；③酸奶、新鲜水果、坚果、干枣、葡萄干、柿饼等各种天然食物才是零食首选','2015-03-15 10:04','1','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美国ARIIX爱睿希健康管理','false','【冬季感冒“五吃五不吃”】冬季是感冒高发期,对抗感冒你有经验吗? @健康时报 说~感冒期间最好不要吃甜食、高盐食品、粗纤维食物、辛辣食物、浓茶、咖啡。可吃点清淡的,如汤、粥、清蒸的鱼等，白萝卜、洋葱及大蒜等也可适度吃些。下次别人感冒别再光叫人喝热水啦 !快转给你关心的TA。','2015-01-15 09:18','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美国ARIIX爱睿希健康管理','false','【冬天多吃绿豆芽】冬天肥甘厚腻的食物容易吃得多，经常吃点绿豆芽，解油腻，也能祛火。绿豆芽水分含量也高，很适合冬天食用。此外，绿豆芽中丰富的叶绿素能有效分解致癌物亚硝胺，预防结肠癌。叶绿素还能缓解香烟中有害物质的刺激，保护胃黏膜，对吸烟者来说，应多吃富含叶绿素的蔬菜。','2015-01-15 09:17','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('陈柳男营养师','true','#止泻神器—苹果# 苹果含有鞣酸和果胶，鞣酸是肠道收敛剂，它能减少肠道分泌而使大便内水分减少，从而止泻。其在果肉及果皮内均含有，果皮中含量更丰富。而未经加热的生果胶有软化大便缓解便秘的作用，煮过的却具有收敛、止泻的功效。果胶含在果肉内，近皮处丰富。因此连皮一起吃的煮苹果止泻效果更好。','2015-03-13 14:56','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('陈柳男营养师','true','#白领一族如何吃好三餐#1早餐选择如西红柿、猕猴桃、苹果等蔬果，每天带上1-2种在上午时加餐。2买点健康零食放在办公桌上。如坚果、大枣、酸奶、牛奶等零食，在饥饿的时适当吃一些。3午餐少吃盖浇饭及高热量的茄子土豆类，可以选择菌类、叶菜类、水产类，吃饭速度慢点。4晚餐要清淡简单，每天多运动。','2015-01-14 09:52','3','0','2','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('陈柳男营养师','true','#如何健康洗菜#洗菜时用盐水浸泡或醋都无法去除农药，洗菜浸泡不要超过15分钟，果蔬清洁剂使用不当会残留化学物质。可能促发老年痴呆症、肝脏病变、肿瘤等。农药分水溶性和脂溶性。水溶性农药可以用清水洗净。用柚子皮（橘子皮）+盐+小苏打+水制成魔法清洁剂，它能有效去除蔬菜的农药残留。','2015-01-05 11:36','1','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师杨文娇','true','和儿子一起参观北京通州朵朵鲜生态蘑菇园，看到相当壮观的蘑菇生长景象，感谢中国学生健康报组织的这次有意义的小记者活动。','2014-08-16 12:32','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容David','true','【护肤小知识】香蕉皮，护肤有大用，可用香蕉皮内侧贴在脸上(皮的内侧朝脸皮的一面)，晾10分钟左右，再用清水洗净，可使皮肤变得滋润光滑；治瘊子：用香蕉皮敷在疣(俗称瘊子)的表面，使其软化，并一点点地脱落，直至痊愈；治皮肤瘙痒症,香蕉皮中含有蕉皮素可以抑制细菌和真菌滋生。','2015-03-07 07:40','4','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容David','true','【常喝葡萄籽可美白】每天晚上临睡前，用37度的温牛奶泡葡萄籽饮用，每一次喝的时候，可以剩下一点点用面膜纸浸泡其中，之后敷在面部。这样内调外敷效果加倍，坚持使用一段时间美白效果很明显。葡萄籽中富含维生素、亚麻油酸及多种氨基酸等等，预防黑色素沉淀，降低紫外线伤害，从而达到美白亮肤功效。','2015-03-03 18:41','3','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('kimi高级护肤讲师-小娟','true','热门 【苹果醋】苹果醋是一种天然的收敛剂，可收紧毛孔，清洁皮肤脏污，保持皮肤干净。洗完脸后，可在干净化妆棉上滴入苹果醋，涂抹在脸部，尤其是T区。残留的化妆品或脏污都会留到化妆棉上。不用担心，醋干了以后，不会留下任何气味，毫无痕迹。','2015-03-11 12:07','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('kimi高级护肤讲师-小娟','true','【去黑头方法】将苹果洗净去皮，切成小块放入搅拌机中搅拌成泥。将肌肤彻底清洁干净后，将苹果泥均匀敷于脸部皮肤，尤其是黑头处更要敷上厚厚的一层，保持15-20分钟，用清水将脸部洗净即可，坚持这个方法使用20天，效果显著。不但能去除黑头，还能美白肌肤，具有抗氧化功效，可使肌肤变得细致年轻。','2015-03-17 16:06','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('kimi高级护肤讲师-小娟','true','【这些食物越吃越白】】①豌豆-具有去黑黯、令面光泽的功效；②三文鱼-防止皱纹产生，避免皮肤变得粗糙；③西红柿-所含番茄红素可使皮肤细嫩光滑；④黄瓜-清洁美白肌肤，消除晒伤和雀斑，缓解皮肤过敏；⑤猕猴桃-富含维生素C。','2015-03-17 14:06','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('kimi高级护肤讲师-小娟','true','【柚子】柚子有“天然水果罐头”之称。它含有非常丰富的蛋白质、有机酸、维生素以及钙、磷、镁、钠等人体必需的元素，这是其他水果所难以比拟的。现在的很多清洁和营养面膜中也加入了柚子的成分，经常使用柚子美容对祛痘还有很好的功效。','2015-03-16 18:06','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('kimi高级护肤讲师-小娟','true','【去痘印方法】材料：红酒适量、白醋适量，清水少许。做法：将适量的红葡萄酒和醋倒入一杯清水中，用勺子将溶液混合均匀，洁面后，轻拍于长痘痘的地方。功效：食醋不但具有出色的清洁功效，还能能杀菌消炎，与红酒结合来擦拭痘痘，可有效淡化痘印，效果不错。','2015-03-16 08:07','0','3','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('kimi高级护肤讲师-小娟','true','【牛奶蜂蜜面膜】牛奶适量，蜂蜜少许，面膜纸一片。做法：将牛奶与蜂蜜一起混合均匀，然后加入少量的清水调至合适的浓稠程度，浸入纸膜。洁面乳清洁脸部，然后把此牛奶蜂蜜面膜敷于脸上，等到二十分钟，然后把面膜揭开，清水洗净即可。功效：不但能美白细致肌肤，同时还能改善暗沉粗糙肤质，提亮肤色。','2015-03-16 06:06','0','0','4','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤达人--小雨','true','【水果榨汁，哪种减肥功效较好？】苹果、黄瓜、西红柿都是不错的选择，它们不仅含有较高的水分，还含有较多的维他命C，相对于其他水果的糖分而言并不算太高，但是口感上却比较香甜。','2015-03-05 15:48','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容师One','true','【自制酸奶抗敏面膜】1、用纯鲜牛奶涂抹全脸，最好连颈部也涂上，并做一些简单的按摩，持续大概一分钟左右。2、用热毛巾拭静牛奶。3、将原味的酸奶涂满面部、颈部，厚度相当于一分钱硬币，静卧20～30分钟后洗净。4、涂上保湿液即可。本款面膜含有大量的乳酸，可以发挥剥离性面膜的功效。','2015-03-07 07:50','0','0','4','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容师One','true','【减肥必看——这就是积少成多的力量】1、用去皮鸡肉代替普通鸡肉，一次可减少110卡，一年可瘦3-6公斤；2、以白水代替酒或饮料，一年可减掉3-7 公斤；3、每天5-6餐低脂少量健康餐，一年可以瘦7公斤；4、在七分饱时停止，一年可以瘦10公斤；5、吃鱼一年瘦4-9公斤。','2015-03-06 21:51','0','1','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容师One','true','【柠檬的好处】柠檬有高度咸性，不仅可以止咳、化痰、生津健脾，对血液循环、钙质吸收有很大帮助，其丰富的维他命Ｃ，能预防癌症、降低胆固醇，还可增强免疫力、促进新陈代谢，延缓衰老等多种保健功效，就养颜美容方面来说，柠檬酸可防止暗沉，活化肌肤表皮细胞并软化角质层，使皮肤变白净光泽有弹性。','2015-03-04 09:51','2','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Acymer-先先老师','true','妍膳美隆重推出内服养颜完美组合，胶原蛋白粉+葡萄籽+针叶樱桃=黄金三剑客。针叶樱桃对皮肤的健康和美丽是很重要的，搭配胶原蛋白粉不仅可以促进维生素吸收，还能恢复肌肤紧致弹滑。葡萄籽是强抗氧化剂，阻止肌肤衰老，与其等细纹产生，不如在出现前就预防！','2015-01-26 09:47','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容Dou仔','true','【牛奶美容法】 牛奶有催眠的神奇功效，除此之外，牛奶敷面对皮肤也是很有裨益的。每天晚上，在我们喝牛奶的时候，可以将剩下的一点牛奶抹在脸上，当睡眠面膜使用。如果还有集中美白的需求，我们还可以在牛奶中掺入珍珠粉，这样使用一段时间后，我们会发现皮肤会变得细腻、白滑很多。','2015-03-03 18:52','0','0','6','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('时尚护肤Carl','true','【啤酒抗皱】啤酒里面的维生素、糖还有蛋白质对护肤也有功效，所以啤酒能增强体质，减少面部皱纹。啤酒酒精含量少，但是里面所含有的鞣酸、苦味酸能刺激食欲、帮助消化和清热的作用。但是喝酒要有度，要是每天喝的烂醉，那就不是在抗皱，而是在加速衰老的脚步。','2015-03-03 03:40','0','5','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('时尚护肤Carl','true','【护肤小知识】1、面部干燥的朋友，可用香蕉皮内侧贴在脸上(皮的内侧朝脸皮的一面)，晾10分钟左右，再用清水洗净，可使皮肤变得滋润光滑。2、治瘊子：用香蕉皮敷在疣(俗称瘊子)的表面，使其软化，并一点点地脱落，直至痊愈。3、治皮肤瘙痒症,香蕉皮中含有蕉皮素可以抑制细菌和真菌滋生。','2015-02-27 06:53','3','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('时尚护肤Carl','true','【女人美肤水果】石榴（减少体内沉积的氧化胆固醇，延缓衰老）；柚子；猕猴桃；大枣（含大量维C）；苹果（补脾气、养胃阴，心血管健康保护神）；柿子（养肺护胃，清除燥火）；板栗（养胃、健脾、补肾）；梨（清热解毒、生津润燥）；橄榄（富含钙质）；山楂（含丰富的黄酮类及维生素）。','2015-02-27 00:53','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容护肤专家Anna5','true','【莹润滋养面膜】材料：杏仁粉、白果粉、珍珠粉、天门冬粉、牛奶、蜂蜜。制做方法：1取相等份量材料放在碗中。2将牛奶与蜂蜜倒入碗中搅拌。3搅拌物敷于面上15分钟。4用清水洗净擦上护肤品。功效：这款莹润滋养面膜保湿效果很好，很适合春季用来对抗肌肤干燥，让肌肤时刻保持滋润，适合敏感皮肤者使用。','2015-03-14 19:43','0','0','2','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师橙子','true','【萝卜汁面膜】做法：将新鲜的萝卜或者胡萝卜汁榨汁，然后将压缩面膜放在萝卜汁里充分浸泡，然后再敷在脸上，同时配合适当的脸部按摩，10分钟后将面膜揭下来即可。萝卜汁具有很好的改善黄褐斑的效果。','2015-03-09 23:28','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('首席护肤讲师Rotation','true','【想要有用完美的胸型你需要的有哪些呢？】①维他命C，防止胸部变形。来自葡萄、西柚等。②维他命E，有助胸部发育。来自芹菜、核桃等。③维他命A，有利激素分泌，来自椰菜、葵花籽油等。④锌、铁、钙、蛋白质等必备的营养素，具有丰胸健美的功效。来自蔬菜、水果等。','2015-03-19 10:10','3','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('首席护肤讲师Rotation','true','【燕麦酸奶去角质】将燕麦研磨成小颗粒，防止划伤皮肤，加入酸奶中混合均匀，敷在皮肤上轻柔按摩，特别在T区稍加按摩。燕麦——含有丰富的维生素B族，是维持肌肤健康与抵御功能的重要成分。酸奶——外敷可让皮肤保湿光亮，且美白的效果也不错。','2015-03-19 06:10','0','0','3','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('首席护肤讲师Rotation','true','【三款冬季护肤佳品】 1、热巧克力：连续喝12周，能让皮肤变得光滑； 2、橘皮红茶：煮茶时加入柑橘皮，可以增加抗癌功效，让皮肤自然健康； 3、胡萝卜汁：1杯鲜榨胡萝卜汁可以保护皮肤免受太阳伤害。 这几天下大雪~~亲爱的，出去的时候注意安全哟~~注意保暖哈~~','2015-03-18 11:31','3','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('首席护肤讲师Rotation','true','【桑椹美容养颜护肤的秘密】作为一种水果，桑椹含有丰富的活性蛋白、维生素、氨基酸、胡萝卜素、矿物质等成分，营养是苹果的5-6倍，是葡萄的4倍，被医学界誉为\"二十一世纪的最佳保健果品\"。这么好的水果，如果能够常吃，不仅可提高人体免疫力，还可延缓衰老、美容养颜。','2015-03-17 06:10','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('韩盈紧致精灵','false','【皮肤最爱的食物TOP8】1、西兰花--增强皮肤抗损伤能力。2、牛奶--增强皮肤张力。3、胡萝卜--保持皮肤润泽细嫩。4、猕猴桃--消除雀斑等斑点。5、大豆--防止色素沉着于皮肤。6、西红柿--展平皮肤新皱纹。7、肉皮--充盈皮肤肤、减少皱纹。8、蜂蜜--保持皮肤红润有光泽。','2015-03-12 20:18','3','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('韩盈紧致精灵','false','#韩盈紧致精灵# 女人养颜要内调，黄瓜所含的黄瓜酸，能促进人体的新陈代谢，排出毒素。维生素C的含量比西瓜高5倍，能美白肌肤，保持肌肤弹性，抑制黑色素的形成。黄瓜还能抑制糖类物质转化为脂肪，对肺、胃、心、肝及排泄系统都非常有益。夏日里容易烦躁、口渴、喉痛或痰多，吃黄瓜有助于化解炎症。','2015-03-06 20:10','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('进帜祈启','true','吃出靓丽的头发： 1. 蛋白质如黄豆，使头发生长 2. 锌如全谷类，改善头皮屑 3. Omega3脂肪酸如核桃鲑鱼，使头皮润泽 4. 维生素B群如酪梨，使头发强韧；5.维生素C如柳橙 猕猴桃，使头发润泽6.矽如土豆 芽菜，防止掉发 7. 生物素如核果 酵母 防止白发 8. Omega6脂肪酸如月见草油、香蕉，头发有光泽。','2014-11-21 04:58','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容护肤专家Anna3','true','【7种方法除雀斑】1、日喝1杯西红柿汁；2、黄瓜粥；3、常饮柠檬汁美白嫩肤，防皮肤血管老化除色素斑；4、常喝黑木耳红枣汤祛斑；5、胡萝卜汁敷脸，或日喝1杯胡萝卜汁，可防皮肤粗糙祛斑；6、禁忌使用含有激素、铅、汞等有害物质的“速效祛斑霜”副作用太多；7、避免刺激性食物，如咖啡、可乐、酒等。','2015-03-12 14:34','4','1','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容护肤专家Anna3','true','【葡萄—抗氧化】葡萄中最具有护肤效果的是葡萄籽。葡萄籽的超强抗氧化能力，是维他命E的50倍，能延缓衰老、维护皮肤健康、阻止紫外线对皮肤的侵袭，有皮肤维他命之称，是维他命C的20倍，其酚花青素，有脂溶性及水溶性的特质，能美白。欧洲称之为“口服的皮肤化妆品”。','2015-03-11 18:35','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容护肤专家Anna3','true','【柠檬】柠檬是水果中的美容佳品，因含有丰富的维他命C和钙质而作为化妆品和护肤品的原料。其主要的美容功效有：增白洁肤、去除色斑、紧肤、润肤、消除疲劳、抗肌肤老化等。柠檬中含有大量的果酸成分，可软化角质层，去除死皮，促进皮肤新陈代谢。','2015-03-11 15:14','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容护肤专家Anna3','true','【五种天然面膜打造好皮肤】（1）牛奶蜂蜜面膜，可深层清洁肌肤，紧缩毛（2）番茄柳橙面膜，可改善毛孔粗大，紧致肌肤（3）酸奶面膜，抚平皱纹，润泽肌肤(4)珍珠菊花粉面膜，既能抗皱与美白，又能有效滋润肌肤（5）橄榄油牛奶面膜，可滑润与平整肌肤，使皮肤有弹性并减少皱纹。','2015-03-11 05:14','1','0','3','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养美容讲师John','true','【DIY柠檬片果酸灼伤皮肤】柠檬片果酸可以使皮肤表皮加速脱落，改善皮肤粗糙情况，但要处理得宜、时间控制精准。如果浓度过强，或施于过敏性和干燥性肌肤便会造成灼伤，也可引致皮肤发炎、色素沉淀等副作用;浓度过稀又没有效果。所以，不建议大家使用。','2015-03-24 02:09','2','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养美容讲师John','true','【黄瓜】黄瓜的补水效果从黄瓜面膜的功效就可见一斑，里面含有的水分可以帮你补充夏季阳光带走的水分，并且让脸感到舒缓和轻松。除此之外，黄瓜性凉、味甘，更有清热、利水、解毒等神奇作用，很适合夏天食用。','2015-03-22 22:38','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Acymer美容师晶晶','true','【草莓草莓鲜美红嫩，能补充肌肤所需水分】草莓中含丰富维生素C ，可智能美白肌肤，对改善黑头、粉刺具有很好的疗效。还能促进消化。草莓中丰富的酸性汁液可以平衡皮肤的PH值。草莓还可以巩固齿龈，清新口气，润泽喉部。','2015-03-11 20:44','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Acymer美容师晶晶','true','【为什么脸上长脂肪粒？】1、身体内分泌失调，面部油脂分泌不均匀同时缺水，毛孔堵塞就出现脂肪粒。调理此种脂肪粒要去专业的美容院进行护理，同时多吃蔬菜水果，多喝水让肌肤通透呼吸顺畅。2、皮肤过于油腻，化妆残留堵塞，护肤品使用不当，无法被肌肤吸收时就会造成毛孔堵塞，轻度就会出现脂肪粒。','2015-03-11 07:24','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容讲师--小烁','true','【夏季吃玉米9大养生功效】玉米素有长寿食品的美称，它浑身都是宝：①健脾益胃、利水渗湿。②抗衰老。③防止便秘，促进胆固醇代谢，加速肠内毒素的排出。④有助预防肿瘤。⑤预防脚气病。⑥美肤护肤。⑦通便减肥。⑧利胆。⑨止血。','2015-03-14 18:12','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容讲师--小烁','true','【苹果】护肤功效：苹果中含有丰富的有机酸，有着非常好的去油脂功效，能够促进皮肤的新陈代谢，帮助排清毒素；另外蜂蜜和鸡蛋又具有极佳的保湿功效，橄榄油的滋润效果是大家都知晓的。因此这款自制水果面膜特别适合干性肌肤的朋友使用。','2015-03-12 06:12','3','0','0','4','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('时_尚美肤Jacob','true','【节后如何减肥？】白菜豆腐汤。材料：白菜心1斤、冻豆腐一块、红青柿子椒各100克、黄瓜汁、盐、鸡精适量。做法：1、将白菜心红柿子椒丝，冻豆腐用精盐腌20min后加入味精和黄瓜汁。2、煮开水后放入材料。功效：大白菜粗纤维丰富，可促进肠壁蠕动，稀释肠道毒素，既能治疗便秘，亦利用营养吸收。','2015-03-20 11:05','3','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alfred','true','【燕麦酸奶去角质】将燕麦研磨成小颗粒，防止划伤皮肤，加入酸奶中混合均匀，敷在皮肤上轻柔按摩，特别在T区稍加按摩。 燕麦含有丰富的维生素B族，是维持肌肤健康与抵御功能的重要成分。 酸奶也是绝佳的美容圣品，外敷可让皮肤保湿光亮，效果也不错。','2015-03-22 11:53','0','0','3','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alfred','true','【方法除雀斑】1、日喝1杯西红柿汁；2、黄瓜粥；3、常饮柠檬汁美白嫩肤，防皮肤血管老化除色素斑；4、常喝黑木耳红枣汤祛斑；5、胡萝卜汁敷脸，或日喝1杯胡萝卜汁，可防皮肤粗糙祛斑；6、禁忌使用含有激素、铅、汞害物质的“速效祛斑霜”副作用太多。','2015-03-22 07:38','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alfred','true','【有痘痘这些东西不能吃！】1.刺激性食物。葱、蒜、辣椒等都容易刺激人体内分失调。2.高油脂食物。油条，煎包，薯条等不易消化食物。3.高糖食物冰淇淋，甜点，糖果等。4.禁烟酒。长期吸烟喝酒，易造成人体内部器官的排毒能力下降，导致体内的毒素长期堆积，易引发痘痘。','2015-03-22 03:21','1','1','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alfred','true','【护肤小知识】西红柿中含有果酸成分，具有清洁毛孔、清洁油污的效果，使得肌肤更加洁净，也能消灭脸上的细菌，彻底清洁皮肤，预防痘痘、粉刺的生长，也不会破坏肌肤的酸碱平衡，保护肌肤，非常适合油性肌肤使用。方法：将西红柿榨成汁，与蛋白、蛋白粉混合，用来洗脸。','2015-03-22 01:14','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alfred','true','【去角质是美白护肤的基本步骤之一】菠萝的果肉不仅具有去毒美白的功效，更可消除肌肤上老化的角质，防止毛孔堵塞，是超强的除垢能手。做法：首先将一片新鲜菠萝压烂，然后敷在身上干燥部位，等15分钟之后再清洗掉，便可把死去的皮肤细胞去掉。','2015-03-21 18:49','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家高级护肤讲师-哆哆','true','【葡萄面膜】功效：葡萄面膜有延缓肌肤衰老的作用，因为葡萄的皮、果肉和核中都含有抗氧化作用的花青素。准备材料：有机葡萄10颗、面膜纸自制面膜方法：1.将葡萄清洗干净，然后捣烂。2.将面膜纸浸在葡萄泥中，完全浸透之后再将面膜纸敷在面部就可以了。','2015-03-11 00:08','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家高级护肤讲师-哆哆','true','【水果吃出水润肌肤美容护肤必学】另外，草莓比较适合于油性皮肤，具有去油、洁肤的作用，将草莓挤汁可作为美容品敷面。现在的很多清洁和营养面膜中也加入了草莓的成分，例如稀有的莓多酚因子，对祛痘还有很好的功效。','2015-03-10 06:09','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家护肤讲师--Alina','true','【引发长痘的3种身体原因及对策】1.肠胃功能较弱。增加肠胃功能，平时可多食用卷心菜、话梅、芋头或白萝卜等；2.雄性激素分泌增多。激素平衡絮乱时应发散压力、积极食用含异黄酮的纳豆、豆腐、豆奶等大豆制品；3.毛孔堵塞。每人长痘相同点就是毛孔堵塞。','2015-03-15 15:32','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家美容护肤讲师-andrea','true','【夏季痘痘肌肤吃什么水果好? 】夏季水果中，桃是营养价值很高的水果。桃富含多种维生素、矿物质及果酸，另外还含有部分果胶、糖分和少量蛋白质。桃的含铁量相对较高，铁是人体造血的主要原料，有助于防治贫血，桃仁提取物有抗凝血作用，并能抑制咳嗽中枢而止咳。同时能加速创口的愈合，改善痘痘肌。','2015-03-08 09:11','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家美容护肤讲师-andrea','true','【生活恶习养出痘痘】1.摸脸/托腮：手上细菌被带到脸上易致长痘;2.不爱吃蔬果常吃肉，不易排毒易囤油脂会长痘。多吃花椰菜，胡萝卜有助消除痘痘;3.常吃洋快餐/方便面：易致便秘，这也是长痘诱因之一;4.喝水少：缺水致水油失衡，皮肤自然就会把多余的油脂分泌出来而引起毛囊堵塞长痘;5.熬夜加重内火。','2015-03-08 05:10','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家美容护肤讲师-andrea','true','【水果护肤帮手-葡萄】葡萄籽富含一种营养物质“多酚”，是最强而有力的抗自由基分子，其抗衰老的能力是维生素E的50倍，是维生素C的25倍。葡萄的果肉里有活力的功效：镁+维他命B群，可以促进肌肤的活力，使肌肤散发明亮清新的年轻光采。','2015-03-07 09:10','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Albert','true','【夏季健康生活一天中的6个黄金时间段】①饮茶最佳时间：用餐1小时后；②喝牛奶最 佳时间：晚上睡觉之前；③吃水果最佳时间：饭前1小时；④护肤最佳时间：晚上临睡 之前；⑤晒太阳最佳时间：夏季下午4点后；⑥体育锻炼最佳时间：傍晚运动可使体内 化解血栓的能力增加39%。','2015-03-24 18:00','1','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Albert','true','【美容水果：草莓】草莓是维他命A含量丰富的一种水果，在一定程度上可以增强皮肤弹性，并且起到保湿的功效。除此之外，草莓还具有去油、洁净肌肤的作用，很适合油性皮肤的女性朋友们。入睡前饮一杯草莓汁还能令神经松弛，对治疗失眠效果非常好的。','2015-03-22 23:41','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Albert','true','【光影+面积感的铜棕色面颊】铜棕色腮红会给人酷酷的感觉，也会更有青春气息哦，将腮红轻轻扫在脸颊两侧，并向苹果肌下方延伸一点，在营造好气色的同时，还有瘦脸的效果！如果使用铜棕色腮红，在唇妆上最好也用同色系，这样整个人会立刻散发健康的气息。','2015-03-21 22:17','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家护肤师-哆哆','true','【节食减肥，小心脱发！】长期节食减肥，体重也许会大幅下降，不过头发也有可能会大巴脱落！因为缺乏锌、蛋白质等营养成分会导致头发脆弱，容易断裂、脱落。所以减肥期间，建议多吃富含蛋白质的食物，比如鱼类和豆类，而蔬菜和胡萝卜也是加强头皮角质层健康的不错选择，可以多多食用！','2015-03-21 22:22','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('sindy巧巧','true','【冬季吃火锅 少吃5类菜】①高能量菜品，易增肥：肥牛、肥羊、羊尾；②高胆固醇，易增加心脑血管疾病风险：猪脑、肥肠等内脏类；③高盐分：午餐肉、香肠、鱼丸、牛肉丸；④可能用甲醛等化学品处理的菜品：毛肚、百叶、黄喉、鹅肠；⑤易染色食品：鸭血、海带、金针菇、鲜竹笋。','2015-02-19 17:56','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('sindy巧巧','true','【祛痘美白的方法】①外敷内养,清洁消炎,滋润补水是基础,定期进行体内排毒,可以增强祛痘美白功效;②珍珠粉,酸奶去痘印,珍珠粉不要放得太多,容易堵塞毛孔.酸奶尽量低脂或者脱脂,避免养分过多产生脂肪粒;③自制祛痘面膜,黄瓜打碎与蜂蜜,绿豆粉,面粉调成糊状,洁面后均匀涂抹敷脸20分钟即可;','2015-01-30 20:36','1','0','2','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美美容师Abby','true','【美容帮帮忙】夏季是属于水果的季节，我非常推荐西柚减肥法，因为西柚中含有宝贵的天然维生素p和丰富的维生素c以及可溶性纤维素，是含糖分较少的水果。而且维生素p可以增强皮肤及毛孔的功能，有利于皮肤保健和美容，而且口味也是酸酸甜甜的，这哪称得上是减肥啊，明明是享受嘛！！','2015-03-22 23:43','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美美容师Abby','true','【年轻秘诀】1.多吃葡萄，葡萄、李子和梨中都含有山梨糖，它是一种保湿剂，有助于皮肤吸收水分和保持湿润。2.多吃红色，富含番茄红素的西红柿等红色食物可帮助皮肤防御紫外线伤害。3.经常锻炼，每周至少锻炼4次，有助于防止肌肉萎缩和改善睡眠。','2015-03-20 11:43','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容老师juoke','true','【草莓】增白和滋润保湿。草莓属浆果，含糖量高达6%-10%，含多种果酸、维他命及矿物质等，可增强皮肤弹性，有美白和滋润保湿的功效。草莓较适合油性皮肤，有去油、洁肤的作用，草莓挤汁可作美容品敷面。很多清洁和营养面膜中有草莓成分，如稀有的莓多酚因数，有很好的祛痘功效。','2015-03-23 18:32','5','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容师goirl','true','【深藏不露的食物祛斑高手，不要错过】大豆中所富含的维生素E能够破坏自由基的化学活性，不仅能抑制皮肤衰老，更能防止色素沉着于皮肤。 将黄豆、绿豆、赤豆各100克洗净浸泡后溷合捣汁，加入适量清水煮沸，用白糖调味做成饮服。每日3次对消除黄褐斑很有功效。','2015-03-14 11:33','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容师goirl','true','【很多女性都喜欢果蔬美白】觉得其天然美白成分新鲜又经济，但专家们建议果蔬美白只适用于夜晚，因为植物具有光敏感性，会吸收紫外线。如果只是直接敷用有时反而会变黑。相比果蔬美白，专业美白产品就安全得多，专业美白产品所添加的水果成分，多半是经过萃取，保留了植物精华，却没有光敏感这类问题。','2015-03-12 19:33','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容师-小烁','true','【早春美白护肤小“妙”招】洗脸时在洗脸水中加入10滴左右的白醋，可以美白润肤，还能抑制油脂分泌。可以用维生素C片（最便宜那种）两粒泡水当喷雾，肌肤会变得又嫩又白。平时要多多喝水，吃新鲜的蔬菜水果，多多保持睡眠，健康的生活方式更能赢来白皙健康的漂亮肌肤。','2015-03-16 12:20','2','0','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容师-小烁','true','【玉米煮得越久，越抗衰老】玉米有7大养生功效：1.抗衰老；2.健脾益胃；3.防动脉硬化；4.防癌；5.美肤护肤；6.降糖；7.通便。其中对眼睛的抗衰老功效尤其显著，而且加热时间越长的玉米，抗衰老作用越好。所以喜欢玉米的朋友不妨煮一锅玉米汤，既能喝汤暖身，又能抗衰老，一举两得。','2015-03-15 11:21','5','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容师-小烁','true','【草莓的营养价值 滋补美容护肤佳品】草莓的营养分析：每百克草莓含维生素C50-100毫克，比苹果、葡萄高10倍以上。科学研究业已证实，维生素C能消除细胞间的松弛与紧张状态，使脑细胞结构坚固，皮肤细腻有弹性，对脑和智力发育有重要影响。饭后吃一些草莓，可分解食物脂肪，有利消化。','2015-03-13 13:30','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容师-小烁','true','【草莓的营养价值 滋补美容护肤佳品】草莓的药用和医疗价值：鲜草莓汁可治咽喉肿痛。食草莓划，对积食胀痛、胃口不佳，当地人将其茎叶捣碎用来敷疗疮有特效，敷蛇咬、烫伤、烧伤等也很奏效。草莓汁还有滋润营养皮肤的功效，用它制成各种高级美容霜，对减缓皮肤出现皱纹有显著效果。','2015-03-13 05:10','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容师-小烁','true','【葡萄】护肤功效：葡萄里面含有的葡萄酚（花青素）具有极佳的抗氧化作用，能够预防肌肤的氧化衰老以及皱纹的产生。在抵御紫外线侵袭方面也有着不错的作用。另外它在修复受伤胶原蛋白和弹性纤维，可以让肌肤变得更加健康和白嫩。','2015-03-13 01:01','3','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容师-小烁','true','【葡萄】护肤功效：葡萄美容是最近几年流行起来的水果美容方法，因为它含有丰富的花青素，在抗氧化方面有着极佳的作用。所以经常敷用此款面膜能够起到延缓肌肤衰老的作用哦。','2015-03-12 20:51','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容师-小烁','true','【柠檬】护肤功效：柠檬中含有丰富的维生素c，是美白的佳品，而且天然安全，不用担心副作用。它在淡斑、嫩白和抗衰老方面都有着不错的疗效。','2015-03-12 16:40','2','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容师-小烁','true','【香蕉面膜】护肤功效：此款面膜同样适合干性肌肤的妹纸使用哦。香蕉中含有丰富的营养成分以及面膜那种粘稠的质地，对于干性皮肤的滋养和修复是非常有效的。而酸奶有着清洁肌肤和收缩毛孔的作用，只要坚持一段时间，就能让你的肌肤变得白皙细腻哦。','2015-03-12 12:30','2','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('护肤美容师-小烁','true','【盛夏6款瘦身减肥美食】1.鳄梨：添加饱腹感、减少食量；2.大豆：无益心脏健康、消弭便秘；3.黄瓜：美容护肤、美体；4.芦笋：促进肠道蠕动、预防大肠癌；5.豆芽：延缓饥饿感、减少皮下脂肪、预防癌症；6.圆白菜：排毒、抗氧化、美肤。','2015-03-10 10:30','3','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容-Alfred','true','【蔬菜美白祛斑法】所需材料：苦瓜、青椒、青苹果制作方法：以上三种材料取同等量，不太爱吃苦瓜的MM，可以少放点哦，一起用果汁机榨汁，每天喝一杯。然后用面膜纸沾取美容蔬菜汁，敷在脸上，20分钟后洗去。有显著的美白祛斑功效。','2015-02-27 12:12','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容-Alfred','true','【番茄蜂蜜面膜】材料：番茄一个，蜂蜜适量做法：将番茄去皮榨汁，加入蜂蜜搅拌均匀。清洁好脸部后，将做好的面膜均匀的涂抹在脸上，15分钟后用清水洗净即可。功效：可以有效美白祛斑，滋润肌肤。','2015-02-26 23:42','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容-Alfred','true','【自制DIY柠檬面膜】护肤品环保又经济方法：柠檬中的柠檬酸是天然美白和排毒物质。蛋清有助于提高皮肤的自身防御能力，蜂蜜具有抗菌与保湿功效。把以上的所有材料混合搅拌至均匀后抹在清洁后的皮肤上，停留15分钟。再用清水轻柔的洗掉面膜。','2015-02-26 02:52','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级护肤讲师橙子','true','【番茄蜂蜜美白面膜】材料：番茄1个，蜂蜜1汤匙，面粉10克；制作方法：番茄放入榨汁机榨汁，将榨汁倒入碗中，加入蜂蜜和面粉调成糊状。每周2-3次；使用：洁面后，将调好的面膜涂抹于面部和颈部，20分钟后用温水洗净，再拍上柔肤水。功效：滋润皮肤，美白祛斑。','2015-03-07 09:07','3','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容讲师John','true','【瘦大腿冠军--白萝卜】白萝卜所含有的辛辣成分--芥子油，能够促进脂肪类物质更好地进行代谢，避免脂肪在皮下堆积。这个功能，是任何一种蔬菜和水果都比不上的。女性利用白萝卜的这种功效来对付大腿部的脂肪再合适不过了。','2015-03-03 20:46','5','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容讲师John','true','【瘦小腹冠军--番茄】女性的肠道中如果堆积了太多的废物，就容易形成小肚腩。番茄所富含的食物纤维，可以吸收肠道内多余的脂肪，将油脂和毒素排出体外。 女性在饭前吃一个番茄，可以在一定程度上起到抑制肠道吸收脂肪的作用。长期坚持下去，你便会远离小肚腩的困扰。','2015-03-03 12:26','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('R_Lee_儒晨纯','true','怎么吃早餐，身体更健康#：①缺了主食:增加面包、馒头等主食；②西式快餐:应加上水果或蔬菜汤；③油条豆浆:一星期不宜超过一次；④路边购买的早餐:一是要注意卫生，二是最好买回去吃；⑤剩饭菜:尽量别再吃，如果要吃一定先热透','2015-02-22 23:59','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容讲师Aidan','true','【多吃黄绿色蔬果】叶黄素和玉米黄素属于色素复合物大家族一类胡萝卜素的成员。这两种色素不仅给食物带来鲜艳明快的色彩，当两种色素进入体内后，它们将在眼睛后部的光敏感组织中积累，这些组织通过过滤紫外线中的蓝光对眼睛起到保护作用。','2015-03-01 22:07','1','0','0','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容讲师Aidan','true','【胡萝卜汁涂脸】将准备好的新鲜胡萝卜用水洗净，然后放进水果机中榨汁，在清洁完面部之后，将其涂抹在脸上，轻轻拍打帮助肌肤吸收，待干后再用植物油轻拍在面部即可。每天早晚各一次，长期使用这个方法，可以改善皮肤粗糙及雀斑。','2015-02-28 06:07','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容讲师Aidan','true','【胡萝卜汁】将鲜胡萝卜研碎挤汁，取10毫升-30毫升，在每天洁面后，把胡萝卜汁轻轻的拍打在脸上，直到被完全吸收后，再用涂有植物油的手轻拍面部，一周使用一次，不仅能够改善肌肤的粗燥状态，还能够有效消除雀斑。','2015-02-28 02:48','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容讲师安娜老师','true','越吃越白的五种食物——①豌豆-豌豆具有“去黑黯、令面光泽”的功效；②三文鱼-防止皱纹产生，避免皮肤变得粗糙；③西红柿-番茄红素可使皮肤细嫩光滑；④黄瓜-清洁美白肌肤，消除晒伤和雀斑，缓解皮肤过敏，是传统的养颜圣品；⑤猕猴桃-富含维生素C，干扰黑色素生成。','2015-01-10 17:30','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Acymer-Beck','true','【五个适用于冬季的美容护肤小常识】1、尽量保持皮肤湿润，最好方法是多喝水，多吃水果。2、选择适当的护肤品 3、冬季也应注重防晒，冬季防晒不容忽视。4、适当的热水浴：能够加强皮肤的新陈代谢，促进血液循环，保持健康的肤色。','2015-01-23 08:21','1','0','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤Dinah','true','【薏仁粉收缩毛孔面膜】用料：薏仁粉2匙、脱脂奶粉1匙、蛋清适量。做法：先薏仁粉、脱脂奶粉加蛋清直至捣成浆状；清洁脸部后，涂于脸部,轻轻按摩以便肌肤吸收养分，１５分钟后用清水洗净。 功效：本面膜对长粉剌及皮肤粗糙的人适用，效果极佳；可以瘦脸、毛孔变细，增加皮肤光泽度。','2015-03-24 15:46','0','0','2','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤Dinah','true','【10款瘦身食物 想要窈窕就靠它啦！】1.鳄梨：增加饱腹感。2.大豆：有益心脏健康、消除便秘。3.黄瓜：美容护肤。4.芦笋：促进肠道蠕动。5.豆芽：延缓饥饿感。6.圆白菜：排毒、抗氧化。7.红灯笼椒：预防结肠癌。8.玉米：排毒。9.海苔：延缓衰老、加快新陈代谢。10.鲑鱼：提升新陈代谢、降低食欲 。','2015-03-23 06:47','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('somuch芳','false','【女性必知的7个健康常识】1、皮肤不好是食盐过多；2、经期腹泻不是正常现象：内分泌水平失衡所致；3、紧急避孕药只能一年吃一次：卵巢肝肾代谢紊乱；4、每日化妆会增加患癌概率；5、黑眼圈可能跟妇科疾病有关；6、每天一杯豆浆能去掉小腹：阻断糖变脂肪过程；7、辣椒是好补品：VE并加速代谢。','2015-01-09 22:23','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('somuch芳','false','五大食物助你跟“熊猫眼”说Byebye：1.鸡蛋（优质蛋白质缓解黑眼圈）；2.芝麻（富含对眼球和眼肌具有滋养作用的维生素E）；3.胡萝卜（维生素A维持上皮组织正常机能，改善黑眼圈）；4.海带（富含铁质，增强血红蛋白输送氧分和营养成分的能力）；5.绿茶（多酚抑制黑眼圈）。','2015-01-07 21:31','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤Dinah','true','【冬季补水蔬菜——绿豆芽】我们冬天吃肥甘厚腻的东西过多，经常吃绿豆芽可以帮助祛火解油腻。同时，绿豆芽富含的膳食纤维还可以改善胃肠道功能。当然，绿豆本身没有维生素C，但发芽后就会产生了。同时，豆芽水分含量也高，可以补水。','2015-03-23 03:46','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤Dinah','true','【熬夜多吃5种水果】1、苹果：“每天吃苹果，医生远离我”，苹果含有丰富的果胶，可以促进排泄，防止动脉硬化；2、杨桃：含有对人体健康有益的多种成分，有益皮肤；3、橙子：有利于清肠通便，排除体内有害物质，增强免疫力；4、柠檬：能去斑、防止色素沉着；5、葡萄：对保护肝脏效果非常明显。','2015-03-21 18:46','6','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤Dinah','true','【十大保湿食物】1.西兰花——保持皮肤弹性。2.胡萝卜——富含胡萝卜素。3.牛奶——增强张力、除皱纹。4.大豆——防色素沉着。5.猕猴桃——干扰黑色素生成。6.西红柿——番茄红素。7.蜂蜜——使皮肤红润细嫩8.肉皮——富含弹性蛋白9.三文鱼——防止皱纹产生10.海带——防过多分泌油脂。','2015-03-21 03:47','4','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤Dinah','true','【柠檬片泡水的功效】1、冰柠檬水中可是含有大量的维生素成分，可以来修复到受损伤的肌肤细胞，多喝柠檬水就可以调理肌肤。2、柠檬水酸酸甜甜，可是非常的爽口，而且特别的容易令人开胃。而且柠檬还能防止黑色素的沉积。3、柠檬水也是最经济实惠的爽肤水，可以起到收纳毛孔的作用。','2015-03-20 00:46','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家美容讲师---Amy','true','【饮用充足的白开水】喝500毫升饮用水30分钟后，皮肤中毛细血管的血液流量有所增加。皮肤含有30%的水分，这让它看上去丰满紧致。不要用碳酸饮料或者勾兑果汁来替代白水，只有白开水能将毒素从身体内冲刷出来，让皮肤显出健康的光泽。','2015-02-16 05:48','1','0','0','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家美容讲师---Amy','true','【早餐不要只吃一个水果】一些女性把苹果、香蕉、桃、西红柿当早餐，目的是为了减肥。早餐仅以一个水果代替，不但达不到减肥的目的，还会带来健康隐患。同时，这种早餐既缺乏供给大脑能量的糖源，又缺乏能使人保持旺盛精力的蛋白质，时间久了会引起多种营养素缺乏','2015-02-12 09:48','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美肤之路','false','【荔枝】荔枝含维生素A、B1、C，还含有果胶、游离氨基酸、蛋白质以及铁、磷、钙等多种元素。荔枝有补肾、改善肝功能、加速毒素排除、促进细胞生成、使皮肤细嫩等作用，是排毒养颜的理想水果。','2015-03-21 11:03','4','0','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美肤之路','false','【多摄取新鲜蔬菜和水果】从调节饮食上来帮助肌肤消除红血丝也是一个很好的方法，女生可以尽量每天多吃一些牛奶、豆制品及新鲜的蔬菜、水果，这样能够帮助肌肤增加抵抗力，同时还可以增强肌肤血管壁的韧性和弹性，有效改善肌肤微循环，能够起到缓解和改善红血丝的现象。','2015-03-20 20:06','4','0','1','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容师Anna1','true','【丰胸饮食】女人平时应该多吃些莴笋(能调养气血,促进乳房部位的营养供应,还能改善皮肤的滋润感和色泽.),木瓜,核桃松仁,玉米(富含维生素E,也是饮食专家推崇的健胸食品.),黄豆,青豆和黑豆(富含蛋白质,卵磷脂等物质).','2015-03-12 14:14','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容师Anna1','true','【牛奶面膜】牛奶一直是家喻户晓的美白材料，其实它与面粉一直自制成面膜还增加了保湿滋润的功效。将牛奶和适量的面粉搅拌至糊状，然后均匀的涂抹于面部，待面膜差不多干透后用温水洗净就可以了。面膜功效：具有很好的滋润和美白效果，牛奶能使肌肤恢复弹性，让肌肤看上去更细腻光滑。','2015-03-12 00:53','0','0','4','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤师-烁','true','【夏季水果美容 樱桃护肤功效第一】樱桃的含铁量位居水果之首。维生素含量高于苹果，其他矿物质磷、钾的含量也很高，是最佳的补血益气滋养品。可增强体质、健脑益智，它能使皮肤嫩白光滑，去除黑头，还有去皱除斑的功效。樱桃性温热，不宜多食；热性病及虚热咳嗽者忌食。','2015-03-13 00:13','3','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤师-烁','true','【黄瓜去黑眼圈】黄瓜除了补水保湿护肤功效外，还能自制成眼膜来去除黑眼圈、眼袋、眼部浮肿等肌肤问题，效果也是很不错的。准备一根新鲜的黄瓜，洗净切成小块，然后放入榨汁机中榨取汁液，用化妆棉浸入黄瓜汁里，等到吸满以后取出敷于眼部肌肤，保持15分钟的时间，再用清水洗净就可以了。','2015-03-12 11:42','4','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤师-烁','true','奇异果的效果首先是美白。是因为奇异果富含维生素C——研究报告说大概是柠檬中维生素含量的1.4倍，在每100克的奇异果中，维生素C含量是75毫克，而同等量的柠檬仅含有维生素C53毫克。能够促进皮肤健康，保持光泽和弹性。含其它水果少见的营养成份如氨基酸及天然肌醇等，有效修复受损肌肤，可完美护肤。','2015-03-11 06:32','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师-Mr','true','【黄瓜蜂蜜洁面水】黄瓜1/2根，蜂蜜2小匙；做法：黄瓜去皮，切成小块，放入榨汁机中榨汁。然后将蜂蜜加入黄瓜汁中充分搅匀即可。3、取洁面水边涂抹边划圈按摩脸部2分钟，然后用清水冲洗干净。功效：收敛毛孔、清爽控油。保护肌肤免受外界刺激，并可以预防痘痘。','2015-03-13 13:56','4','0','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Monty护肤讲师','true','【天然2个小方法收细毛孔有奇效】1、苹果吸油面膜。功效：苹果中的果酸成分能吸走面上多余的油脂。2、薄荷收毛孔面膜。功效：迷迭香有消炎抗氧化功效；薄荷具收敛爽肤作用，敷时感觉阵阵清凉；苏打粉可分解油脂。三合一能发挥收细粗大毛孔的功用。','2015-03-23 06:54','2','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Monty护肤讲师','true','【护肤的八种食物】1、奇异果：含有丰富维C，抗氧化，美化肌肤；2、鸡蛋：维生素H可健康美甲；3、苹果：使皮肤细嫩红润,预防皮趺老化；4、南瓜子：富含锌可消除痘痘；5、菠菜：保护眼睛；6、西洋菜：抗氧化防止脸部水肿；7、绿豆：护发；8、亚麻籽：防止肌肤干燥。','2015-03-20 12:54','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('养生家Paul','true','【5张天然美容面膜助你肤如凝脂】1. 牛奶蜂蜜面膜，可深层清洁肌肤，紧缩毛孔；2. 番茄柳橙面膜，可改善毛孔粗大，紧致肌肤；3. 酸奶面膜，具有抚平皱纹，润泽肌肤的功效；4. 珍珠菊花粉面膜，具有抗皱与美白双重功能，又能有效滋润肌肤； 5. 橄榄油牛奶面膜，可滑润与平整肌肤','2015-02-28 20:20','1','0','3','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('养生家Paul','true','【胡萝卜汁】把新鲜的胡萝卜放入榨汁机中榨汁，每天喝一杯新鲜的胡萝卜汁具有显著的美白效果，由于胡萝卜中含有丰富的维生素A原，它能在我们的体内转化为维生素A，它具有滋润和强健皮肤的功效，对于粗糙的肤质和长斑的皮肤具有很好的美容效果，绝对让原本的暗黄皮肤迅速美白起来。','2015-02-27 19:21','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容师Er','true','【健康吃晚饭四个提示】1.临睡前两小时内不宜吃晚饭。2.吃过晚饭不要在吃宵夜，如果晚上有工作者，可以备一些全麦面包，酸奶，水果，但适量吃。3.尽量不要在外面吃晚饭。4.吃清淡的食物，不要吃口味重的食物作为晚饭。','2015-01-23 12:14','1','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('养生护肤师Ken','true','【美白方法】胡萝卜汁：把新鲜的胡萝卜放入榨汁机中榨汁，每天喝一杯新鲜的胡萝卜汁具有显著的美白效果，由于胡萝卜中含有丰富的维生素A原，它能在我们的体内转化为维生素A，它具有滋润和强健皮肤的功效，对于粗糙的肤质和长斑的皮肤具有很好的美容效果，绝对让原本的暗黄皮肤迅速美白起来。','2015-03-01 15:57','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('养生护肤师Ken','true','【平衡饮食】既然想减肥，那么少吃一点肯定要的。要学会平衡饮食，不吃高脂肪的食物，多吃蔬菜水果，不暴饮暴食，宵夜也尽量少吃。平时假如实在是嘴馋，那就要学会选择合适的零食。将薯片那些东西都收起来吧，吃点核桃，这样不仅有助于减肥，而且还能美容保健哦。','2015-02-28 04:58','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容护肤专家Anna2','true','【美白达人护肤法】① 每周1次去角质，每周2张美白面膜。②食用含维生素C的食物，如柠檬、猕猴桃等。③无论在室外还是在室内都要做好防晒，特在室内避免电脑辐射，日光灯的紫外线污染。④如果天生拥有健康肤色，那就好好接受本来的肤色，让美白护肤品针对于透亮，局部暗沉来解决问题。','2015-03-15 00:00','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容护肤专家Anna2','true','【苹果颜色各异保健功效不同】苹果颜色可以预示保健功效，比如红苹果更有益心脏，提高记忆力，保持泌尿系统的健康；青苹果更利于牙齿和骨骼强壮；黄苹果则可强健人体免疫系统，对预防某些癌症也有好处。黄苹果对保护视力有很好的作用，经常使用电脑的上班族可适当进食。','2015-03-13 14:39','4','0','0','5','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容护肤专家Anna2','true','【嘴唇的周围也是爱出痘的地方】发现嘴唇周围有痘痘冒出后，先观察一下自己是否便秘，一般情况就是因为便秘引起的出痘。如果不改善便秘的情况，就会导致体内毒素堆积，从而引出痘痘。生活上要有良好的饮食习惯，不要让宿便的养成损害你水嫩光滑的肌肤，多吃富含纤维的蔬菜水果，养成定时排便的好习惯。','2015-03-12 22:00','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容护肤专家Anna2','true','【生活减肥小妙招】喝大量的水12-16杯。尽量多喝，不要喝咖啡，可以多喝绿茶、乌龙。多喝水可以帮助身体排除有毒物质，还可以帮助“融化”脂肪。牛奶豆浆不能算是水，但是你喝不够12-16杯的话，它们就可以勉强凑数。你会觉得上厕所次数很多，也是强迫自己起来多活动的一个办法。','2015-03-11 15:59','0','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容讲师Anna老师','true','日常的饮食习惯也与肌肤的好坏有一定的联系。干燥的季节除了要多喝水之外，还要多吃水果蔬菜，补充体内和皮肤的水分流失。合理的饮食结构也可以改善人的体质，帮助延缓衰老。由于人的体液呈现弱碱性，所以多摄入碱性食物如蔬菜、瓜果、豆制品等，可以使皮肤细腻光滑。皮肤质询qq：859-369-429','2015-03-11 19:04','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容讲师Beck','true','【眼部保养小妙招】牛奶是常见的美容佳品，对美白，湿润具有一定的作用，即可外敷也可内调，取一定量的牛奶和水一起混合，然后放入冰箱里冰冻几分钟，用化妆棉沾湿，把它敷在眼部四周，并进行5-10分钟的按摩，再用水洗净，有效改善黑眼圈的同时，使肌肤光滑，细腻。','2015-03-23 10:11','0','0','2','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容讲师Beck','true','【餐前西红柿 美白好肤色】西红柿是维他命C的 大宝 库，因此是美白的健康食品。VC可以抑制皮肤内酪氨酸酶的活性，有效减少黑色素的形成，从而达到使皮肤白嫩，黑斑消退的效果。韩国明星崔真实就是西红柿美白大使，很多明星也曾透漏了西红柿美白的秘方','2015-03-22 23:27','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('首席护肤顾问Anna','true','【DIY苹果去皱面膜】苹果是天然的美容佳品，用苹果来做面膜也很好哦，而且做法简单。做法：苹果洗净，去皮去核，然后切成薄片，清洗脸部后把苹果片均匀地敷在脸上，等苹果皮片慢慢变干后取下来。功效：这款苹果面膜可以充分除去肌肤褶皱、给肌肤足够的养分。','2014-12-25 11:41','7','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alisa','true','内调黄金三剑客】胶原蛋白粉+针叶樱桃+葡萄籽提取物。①淡斑、美白，让皮肤白里透红；②促进胶原蛋白合成，增强皮肤弹性③预防皮肤衰老，逐层提亮肤色④防止色素沉着，击……','2015-01-31 09:00','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家美容Monty老师','true','【健康美白的方法】1.多吃黄瓜、草莓、西红柿、桔子等，因为其含有大量vc，能有效帮助黑色素还原，协助美白，增进免疫力。2.充足睡眠，有效缓解生活压力，多听音乐，也是美白的好帮手。3.少抽烟、少喝刺激性饮料，保证睡眠，可保持肌肤柔嫩光润.','2015-03-23 20:42','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家美容Monty老师','true','【皮肤最爱的食物Top8】西兰花--增强皮肤抗损伤能力。牛奶--增强皮肤张力。胡萝卜--保持皮肤润泽细嫩。猕猴桃--消除雀斑等斑点。大豆--防止色素沉着于皮肤。西红柿--展平皮肤新皱.肉皮--充盈皮肤肤，减少皱纹。蜂蜜--保持皮肤红润有光泽。','2015-03-23 11:43','3','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('伊的家美容Monty老师','true','【葡萄籽提取物】是抗氧化之王，它的功效比维生素高10000倍，好似强大的抗氧化剂盾牌，为肌肤穿上一层厚实的保护膜，减少恶劣环境对皮肤的伤害，还能阻止肌肤里的透明质酸流失，提亮肤色。基础护肤、抗衰老保养双管齐下，轻松拥有好质感肌肤，如果你的皮肤是敏感性的，那更是必不可少的。','2015-03-20 02:42','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Monty护肤美容讲师','true','【有痘痘这些东西不能吃】1.刺激性食物。葱、蒜、辣椒等都容易刺激人体内分失调。2.高油脂食物。油条，煎包，薯条等不易消化食物。3.高糖食物冰淇淋，甜点，糖果等。4.禁烟酒。长期吸烟喝酒，易造成人体内部器官的排毒能力下降，导致体内的毒素长期堆积，易引发痘痘。','2015-03-22 23:39','1','1','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Monty护肤美容讲师','true','【5款美白水“喝”掉脸上斑点】1,每日喝1杯西红柿汁或经常吃西红柿防治雀斑减少黑色素。2,将柠檬搅汁，加冰糖适量饮用。可以白嫩皮肤，消除面部色素斑。3,经常食用黄瓜汁，能消除雀斑、增白皮肤,4，黑木耳红枣汤防止皮肤老化，祛斑黑斑.5,每日喝1杯胡萝卜可防治皮肤粗糙及雀斑。','2015-03-22 22:38','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤老师Julia','true','【远离痘痘】对于饮食和精神因素引起的痘痘6大处方：1，少食用高脂肪类、高糖分食品 、高碳水化合物。 2，过甜的水果。3，辛辣刺激性食物及酒类。4，保持规律的作息时间，少熬夜，保证睡眠质量。5，多吃高纤维食物，多喝水，保持大便通畅。','2015-01-24 04:42','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤老师Julia','true','【葡萄紧肤面膜】葡萄含天然抗氧化剂，加上天然的果酸具有轻微的去角质功效，有效亮白及洁净肌肤。4至8颗小葡萄、蛋清1个。将葡萄去皮去核，将果肉捣碎成浆状，再混合蛋清，然后均匀地涂抹在脸上避开眼部和嘴唇周围，10分钟后用清水洗净。','2015-01-24 01:15','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('资深时尚护肤导师------甘老师','true','【9种日常食物在养生上的效用】1.番茄延缓衰老：生,熟营养不同；2.茶叶防辐射：睡眠不好胃肠差人慎用；3.姜减少色素沉淀；4.醋增强皮肤活力：过敏者低血压慎用；5.黄豆调理雌激素:不好消化；6.米饭防止肌肤干燥；7.菌类调节免疫系统；8.红酒提升气色；9.燕麦可以减少脂肪。','2015-03-16 15:31','1','1','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('资深时尚护肤导师------甘老师','true','【红色的水果养生保健的作用】红色水果包括西红柿、石榴、西 瓜、草莓、樱桃、桑椹等，这类水果富含类胡萝卜素，具有抗氧化作用， 能清除自由基，抑制癌细胞形成，提高人体免疫力。樱桃铁含量高，有促进血红蛋白再生功效；西瓜汁含有多种有益健康和美容的成分；吃草莓可以培养耐心。','2015-03-16 11:31','7','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('资深时尚护肤导师------甘老师','true','【“籽”也有营养】种子是植物繁衍的保证，但你知道吗，它们还有养生、防病的功效喔：冬瓜籽含不饱和脂肪酸，可预防心血管病；西瓜籽味甘性平，清肺化痰；南瓜籽含锌和固醇类，能预防前列腺癌 ；葡萄籽能延缓衰老；石榴籽可以保护关节，淡化老年斑；猕猴桃籽有助于降血脂。你吃过哪些“籽”？','2015-03-14 11:32','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('资深时尚护肤导师------甘老师','true','【茄子怎么吃清热解暑？当然是蒸着吃！】茄子是少有的紫色蔬菜，营养价值是独一无二的。茄子养生功效：①防胃癌。②抗衰老。③降压。④保护心血管、抗坏血酸。在茄子所有吃法中，蒸茄泥最健康。因为①加热时间最短，营养损失最少。②用油最少。③营养吸收最完全。','2015-03-10 11:32','5','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('资深时尚护肤导师------甘老师','true','【养生堂儿童糖果被诉含违禁成分 或产生不良反应】浙江杭州一家长在华润万家超市连锁店内购买了由海南养生堂出品的 “成长快乐牌乳钙混合胶型凝胶糖果”产品。发现该产品中有禁止添加的违禁成分，3周岁以下婴幼儿食用或将产生不良反应。在调解无果的情况下，该名家长将起诉至法院。','2015-03-06 15:32','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('肌肤调理师Eva','true','【干燥+皱纹，吃什么改善】1. 多吃干果、山药、马铃薯、桃仁、红枣、山楂、青梅、蜂蜜等补益脾胃的食物。2. 多吃富含维生素C和维生素B类的食品，如荠菜、苋菜、胡萝卜、西红柿、红薯、金针菜等等。3. 秋季多吃鱼及瘦肉等动物蛋白质，保证氨基酸的供给，可以补充皮脂腺的分泌，维持肌肤正常PH值。','2015-02-12 14:56','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('养生护肤师Des','true','【美白健康护肤小窍门】1、小黄瓜敷脸（美白消炎）、2、蜂蜜柠檬面膜（美白）3、蜂蜜蛋白奶粉面膜（收敛紧实）4、蛋黄护发（滋润）5、豆粉面膜（去油去角质）6、豆腐泥面膜（美白抗氧化）7、柠檬皮或橘子皮（抑制油脂）还有更多美丽资讯可以去美乐活有机美容生活馆哦！','2015-03-03 03:37','3','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('养生护肤师Des','true','【人生最重要的十大健康食物】菠菜 不贫血、皮肤好、保护视力；西红柿 防癌、美白；巧克力 拥有快乐、护齿；马铃薯 护脾胃、消除眼袋；蘑菇 提高免疫力、减 肥；大豆 抑制肿瘤、延缓衰老；鸡蛋 增强记忆、美容；核桃 健脑、润肤；鱼 吃出好心情；橙子 防癌。','2015-02-28 20:38','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('时尚护肤妆容Monty','true','【药食同源：美容护肤话山药】山药外表不算好看但营养丰富，既可作主食又可作蔬菜，还能制成小吃或甜点。此外，山药本身也是一味中药，具有补脾益肾养颜等功效，是很好的进补\"食物药\"对女性特别好。同时还可以有美白祛斑养颜之疗效且对皮肤有弹性，还能增加肌肤光泽和祛斑作用，希望美女们多吃呀！','2015-03-21 15:27','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('时尚护肤妆容Monty','true','【瘦肚子小技巧系列】改变饥饿模式。长时间吃的过多，比如在节假日时，胃部的牵张感受器（向大脑传递吃饱的信号）敏感性降低。暴饮暴食后，应该多吃点健康、低卡路里、低脂肪、高纤维的食物，比如水果、蔬菜和全麦食品等。这些食物能够满足牵张感受器，而不是把你吸引到剩下的食物面前。','2015-03-21 00:28','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师Aimee','true','【美白第五课】因痘痘或伤疤所造成的印痕，因皮肤组织遭受了破坏，除了黑，肤触也较为粗糙。 要改善此类型的色素沉淀，换肤及补水是保养重点。可使用8-15%的果酸乳液或是果酸凝胶，每天晚上洗脸后在色素沉淀处薄薄的擦一次，达到天天温和换肤，并搭配美白保湿品一起使用，就能改善。','2015-03-23 21:07','0','0','1','4','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alger','true','【美容养颜的水果】1草莓——美白和滋润保湿。2桃子——防止色素沉着和皱纹。3樱桃——抵抗黑色素。4菠萝——淡化面部色斑。5.柠檬——美白、抗老化。6.苹果——纤体及淡化黄褐斑。6.葡萄——抗氧化。7.猕猴桃——消除雀斑和暗疮。8.橘子——增强肌肤弹性、抚平面部小细纹。经常吃会有意想不到的效果哦','2015-03-23 07:59','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alger','true','【护肤小知识】1.隔离保湿：每天都要涂抹隔离霜，电脑旁放一个保湿喷雾随时补水；2.每小时起来运动五分钟，可改善血液循环,使皮肤透亮；3.喝胡萝卜汁 抗氧化使肤色红润；4.按照自己的肤质选用洁面乳清洗面部，让皮肤完全放松，自由呼吸！','2015-03-21 04:24','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alger','true','【茶色斑改善小tips】 1.每天吃一片维生素C和维生素E，可达到祛斑的作用。 2.用干净的茄子皮敷脸，一段时间后，小斑点就不那么明显了。 3.将柠檬搅汁，加糖水适量饮用。柠檬中含有大量维生素C、钙、磷、铁等。常饮柠檬汁不仅可美白肌肤，还能使黑色素沉淀，达到祛斑的作用。','2015-03-20 21:57','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤讲师Alger','true','【拯救面部浮肿】①保持充裕规律的睡眠②临睡前两个小时不吃咸食不喝酒③用40℃左右温热水浸浴20分钟④多摄入一些有利尿作用含钾的食物，减少水分在体内的滞留⑤多摄入富含维E铁质食物，如绿色蔬菜豆制品胚芽类食物等，以促进血液循环⑥经常以按摩的方式刺激面部循环消解水肿。','2015-03-20 19:48','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('养生美容讲师Allen','true','【桔子皮美容法】 将桔子连皮捣烂，浸入医用酒精，加以适量蜜糖，放一周后取出使用，有润滑皮肤及去除皱纹的功效。它会起到让你想不到的妙处。','2015-03-14 09:11','0','1','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('养生美容讲师Allen','true','【防晒水果四：柠檬　】柠檬当中含有丰富的维生素C，维生素C最大的美容功效就是美白防晒。所以想在夏季保持白皙的肌肤，每天早上将柠檬切片冲一杯柠檬水喝，能给肌肤起到很强的保护和护理作用。','2015-03-13 13:11','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('秋界瑶进','false','【建筑美白肌底】美白最基础的就是从肌肤底层开始，抗氧化才能从根源阻止糖分和蛋白质的几何，彻底祛除真皮层内的糖化物质。建议此时应该多吃一些含有氨基酸、维生素C、葡萄籽、日本蜜柑果皮成分的抗氧化产品，让肌肤从内而外的排除毒素，透出亮白肌肤。','2015-03-14 12:54','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师Xiaoyi','true','【女性皮肤光泽4秘方】①早晚两杯白开水：早的一杯可清洁肠道，晚的一杯减少色素沉积；②一个西红柿：富含维生素C；③一杯醋：每次洗手后先敷一层醋，保留20分钟再洗掉，可使手部皮肤柔白细嫩；④一杯酸奶，补充钙质，易于吸收。','2015-03-10 16:09','1','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师Xiaoyi','true','【健康养生——四种感冒的食疗方】1.风寒感冒：吃香喝辣能去寒，可进食葱姜糖水等；2.风热感冒：饮食清淡多喝粥，如鲜梨汁大米粥；3.表里两感型：酸性果汁不可少，如山楂汁、猕猴桃汁；4.胃肠型感冒：茶水红糖好搭档，如红糖菊花茶。','2015-03-08 13:41','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师Xiaoyi','true','【健康养生宝典】1、晨起一杯水身体水平衡。2、右侧卧、睡如弓，睡眠品质好。3、只吃七分饱，身体自然好。4、生命在运动中蓬勃。5、音乐舒畅身心，读书增加智慧。6、吃豆，可长寿。7、跟烟、酒说再见。8、爱与性要完美和谐。9、摆脱孤独寂寞就健康。10、勤用脑，防衰老。11、喝茶，可减肥、防癌。','2015-03-08 08:41','0','1','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师Xiaoyi','true','【食品标签里的健康陷阱】买食品时，看食品标签当中是否含有这些字样：麦淇淋、精炼植物油、起酥油、植物起酥油、人造黄油、人造奶油、代可可脂、氢化植物油。这些都表明食物中含有反式脂肪酸，长期摄入易导致肥胖，另外还会增加罹患冠心病、糖尿病、不孕症风险。','2015-03-07 17:41','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师Xiaoyi','true','【哪些食物防脱发】胡萝卜。胡萝卜中富含β胡萝卜素，这是一种强效抗氧化成分，并会在人体中转化成维生素A，对头皮细胞组织的发育有重要作用。人体若缺乏维生素A会影响头皮健康，导致头发变薄。此外，一般橙黄色食物都富含β胡萝卜素，如南瓜、橘子、枸杞等。','2015-03-07 08:09','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养美容讲师-Kellen','true','【燕麦酸奶去角质】将燕麦研磨成小颗粒，防止划伤皮肤，加入酸奶中混合均匀，敷在皮肤上轻柔按摩，特别在T区稍加按摩。 燕麦含有丰富的维生素B族，是维持肌肤健康与抵御功能的重要成分。 酸奶也是绝佳的美容圣品，外敷可让皮肤保湿光亮，且美白的效果也不错。','2015-03-17 17:27','0','0','3','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('首席护肤讲师--橙子','true','【6款美容护肤食物吃出健康与美丽】1、西兰花：保持皮肤弹性。2、胡萝卜：嫩肤抗皱。3、牛奶：美白抗老。4、大豆：保持皮肤弹性。5、猕猴桃：美白淡斑。6、西红柿：抵抗紫外线。','2015-03-07 11:39','3','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('首席护肤讲师--橙子','true','【夏季水果美容樱桃护肤功效第一】樱桃的含铁量位居水果之首。维生素含量高于苹果，其他矿物质磷、钾的含量也很高，是最佳的补血益气滋养品。可增强体质、健脑益智，它能使皮肤嫩白光滑，去除黑头，还有去皱除斑的功效。樱桃性温热，不宜多食；热性病及虚热咳嗽者忌食。','2015-03-07 05:09','3','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('VIVIAN萍子凝希管','true','热门 【迅速赶走亚健康的食物】1.需提高脑力时吃蓝莓；2.想保持年轻时吃葵花子；3.压力过大时吃1杯低脂酸奶或2汤匙混合坚果；4.感觉悲伤时吃菠菜沙拉；5.能量不足时吃什锦果仁；6.不太舒服时吃人参茶；7.保持清醒时吃鸡蛋全麦面包；8、醉酒后应该喝橙汁。','2015-02-25 16:03','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('VIVIAN萍子凝希管','true','【秋季保健分三段】①初秋防湿护肠胃。②中秋防燥多润肺。要多喝开水、淡茶、豆浆、牛奶等流质，此外，可以尝试百合莲子汤，清炒百合。③深秋防寒护血管。心脑血管患者一定要坚持服药，日常饮食中注意多摄入含蛋白质、镁、钙丰富的食物。','2015-03-11 06:32','0','0','1','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('VIVIAN萍子凝希管','true','【黄瓜】它所含的纤维素能促进肠道排出食物废渣，减少胆固醇的吸收。黄瓜中还含有一种叫“丙醇二酸”的物质，可以抑制体内糖类转变成脂肪，有减肥和调整脂质代谢的功效。','2015-03-10 13:21','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('VIVIAN萍子凝希管','true','【不同水果的最佳食用时间】①早上最宜：苹果、梨、葡萄，可帮助消化吸收，有利通便，而且水果的酸甜滋味，可让人一天都感觉神清气爽。②餐前别吃：圣女果、橘子、山楂、香蕉、柿子。③饭后应选：菠萝、木瓜、猕猴桃、橘子、山楂，帮助消化。④夜宵佳选：桂圆安神助眠。','2015-03-10 07:37','7','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('VIVIAN萍子凝希管','true','【天气与健康】冬季冷空气活动频繁，经常冻得手脚冰冷，感觉手脚冻得都已经不属于自己了。怎么办？食疗其实是个不错的选择，但是贵在坚持哟！五大食疗方帮你暖手脚：1、姜汁甜牛奶。2、当归生姜羊肉汤。3、鲫鱼猪血粥。4、生煎猪肝。5、阿胶冰糖煲芝麻。','2015-03-09 08:40','0','0','1','1','milk');
