insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容师-Deborah','true','【西红柿面膜】材料：西红柿半个，橙子半个制作方法：西红柿洗干净切成两半。橙子洗干净去籽切成两半。西红柿和蛏子放入榨汁机里，过滤，留下汁液。使用方法：洁面后，用脱脂棉将汁液涂抹在脸上，静置25分钟，用清水洗干净。功效：收缩毛孔，紧实肌肤，让皮肤白皙。','2015-03-18 04:33','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高级美容师-Deborah','true','【西瓜皮收缩毛孔】材料：新鲜西瓜一块方法：用勺子将西瓜肉刮出，装入入面膜碗内再放于冰箱冷冻20分钟的时间，在这段时间里你可以先把面部皮肤清洗干净，然后再将西瓜泥涂于脸上，保持15分钟后，再用清水洗净即可。原理：西瓜含有丰富的矿物质和维生素，用来敷脸可以起到紧致肌肤，细致毛孔的功效。','2015-03-17 05:13','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养护肤讲师-橙子','true','【注意调节饮食】冬季天气干燥，平时应多饮水，补充肌体和皮肤的水分丧失，另外合理的膳食结构可以改善体质，延缓衰老。由于人体的体液呈弱碱性，所以多摄入碱性食物，如蔬菜、瓜果、豆制品类、牛奶等，可使皮肤细腻光泽。要注意减少酸性食物的摄入，如肉类、鱼类、巧克力、葱蒜等。','2015-03-11 20:53','1','0','1','1','fruit');
