insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Acymer护肤老师小镇-','true','【西瓜净化美白面膜】材料：西瓜皮2块；制作方法：用 将西瓜皮的汁液刮下，倒入碗中；使用方法：洁面后用干净纱布或脱脂棉蘸取西瓜汁液涂抹在脸上，20分钟后，用清水洗净即可；功效：美白、洁净肌肤。','2015-03-21 07:48','3','0','0','1','fruit');
