insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('上海卫康','true','#卫康小贴士# 【电脑族眼睛压力大吃什么】1、西红柿：西红柿有助改善视力；2、海带：可减轻眼内压力，用来治疗急性青光眼有良好的功效；3、珍珠：珍珠性味甘咸寒，用珍珠粉配龙脑、琥珀等配成的“真珠散”点眼睛可抑制白内障的形成的功效；4、黑豆：黑豆能缓解眼睛疲劳，也有助于防止视力下降。','2015-03-23 11:44','2','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('上海卫康','true','#卫康小贴士# 【春吃豌豆，养颜护眼】在春天里，吃豌豆不仅有养颜的功效，还有很好的护眼功效。现代研究表明，豌豆中含有大量的胡萝卜素、叶黄素，对保护视神经、改善视力非常有益，尤其适合经常熬夜用眼、眼睛疲劳干涩的人吃。可以做成胡萝卜豌豆炒牛肉吃，也可以煮一锅香喷喷的豌豆饭哦。','2015-03-19 10:53','1','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国好奶极致定义','true','【9个健康饮食好习惯】1.复合维生素早饭后吃。2.每餐之前喝两杯水。3.把咖啡加在牛奶里，而不是把牛奶加在咖啡里。4.吃完快餐喝一大杯水。5.下午三点，准时加餐。6.橘子带着“白丝”吃。7.每天订个喝水任务量。8.买水果时拿不定主意，就选深色的那种。9.睡前吃些高纤维食品。','2015-03-20 23:00','1','0','2','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国好奶极致定义','true','【上班族保健】自带健康盒饭小妙招：（1）多选择瓜类、根茎类的不宜产生亚硝酸盐的蔬菜（2）将主食、蔬菜、肉分层装盒（3）热菜出锅乘热装盒、密封冷却后置于冰箱内（4）蔬菜焯水，都能再一定程度上减少亚硝酸盐的产生。一些人认为吃不够量的蔬菜可用水果代替，这种观点对吗？','2015-03-19 21:30','5','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国好奶极致定义','true','#极致健康#①早餐最好吃暖的东西，流食比较好。②早餐最佳时间7至8点。③不能空腹喝牛奶，对于肠胃功能影响很大。④面包选全麦的，因为麦麸中含大量的B族维生素还有纤维素。⑤注意摄取维生素、叶酸和铁。','2015-03-17 09:10','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('热门潮流排行榜','false','【常温奶巴氏奶本质差别】常温奶与巴氏奶最本质差别是杀菌方法的不同。前者是将牛奶超高温灭菌后，用六层复合无菌材料灌装，无需冷藏，保质期长达几个月。后者则是在80℃左右经数秒杀菌，需低温冷藏，保质期为1至7天，两者都可以提供优质的钙质和蛋白质，营养上并无明显差别。','2014-08-28 11:50','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('刘_伟_V','false','西和长道镇苹果，产自甘肃西和跟礼县交界处。今天你吃到的苹果，是果农坚持了几十年，精心种植不断改良的品种。数十年来，苹果的种植标准越来越严格，只为今天，你可以吃得放心、吃得开心。小生自开网店卖香甜可口苹果，望各位支持。 甘肃礼县花牛苹果10斤包邮粉脆苹果非山东天水冰糖心苹果','2014-11-28 13:23','7','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('熹熹家爱的小屋','false','【脆皮炸牛奶（16m+）】好吃到根本就停不下来的节奏！牛奶超嫩滑，甜度也适中，如果麻麻觉得油炸不健康可以直接给宝宝吃牛奶糕，也就是省略掉最后油炸那步，但是偶尔吃一下也没关系，只要炸后控干油份再用吸油纸吸干即可！（by：honey小萌主）','2015-03-18 18:18','0','0','3','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('聪明宝宝杂志','true','#宝宝健康# 养肺的水果：1.柑橘榨汁或蜜煎治肺热咳嗽；2.秋梨可治疗肺热咳嗽；3.红软熟柿，治疗口干唇烂、热痢等症；4.石榴捣汁或煎汤饮，清热解毒、润肺止咳；5.葡萄捣汁加熟蜜浓煎收膏，开水冲服治疗烦热口渴；6.荸荠煮熟可治口燥咽干、肺热咳嗽。','2015-03-24 19:02','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师李彩霞','true','藠头具有促进血液循环的作用：藠头又名薤白，2000年前就作为中药使用。它具有温热身体，促进血液循环的作用，所以有助于预防改善血管系统的疾病。藠头用醋腌后再加上红葡萄酒即是红葡萄酒泡藠头。醋具有使血液流动顺畅，软化血管的作用。所以二者合一后我们会获得更好的健康效果。','2015-03-22 10:18','0','2','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师李彩霞','true','【如何防止癌从口入】①吃“鲜”：每日应至少吃400克不同种类新鲜果蔬；②吃“淡”：清淡饮食，控制吃肉量；③吃“粗”：多吃富含膳食纤维的食物；④吃“苦”：苦味食物有很好的防癌功效；⑤吃“酸”：多吃醋、酸奶等。','2015-03-15 10:32','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师李彩霞','true','#红酒泡藠头#藠头具有温热身体，促进血液循环的作用，用醋腌后再加上红葡萄酒即是红葡萄酒泡藠头。醋具有使血液流动顺畅，软化血管的作用。此外还能促进血液中的糖和胆固醇的代谢，所以有助于预防改善血糖和高脂血症等疾病。由于藠头也具有这种效果，所以二者合一后我们会获得更好的健康效果。','2015-03-14 08:43','0','3','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('昆明广播电视台','true','【 吃糖的危害比吸烟还大！】糖摄入多了，会产生饱腹感，不仅带来胆固醇紊乱、癌症、心脏病、糖尿病和脂肪肝等症状，甚至会损害记忆，导致早亡。据统计，长期高糖饮食者寿命短10~20年。生活中很多菜品、水果含糖量远高于想象，要远离这些糖可不容易。快告诉小伙伴怎么吃糖最健康！戳图↓ via央视','2015-03-23 22:00','1','0','0','1','fruit');
