insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('实用晓百科','false','【做菜先过油】做地三鲜、干煸豆角等菜肴时，很多人喜欢先将食材在油里过一下，捞出来再炒，这样做出来的菜色泽明亮、气味香浓。但这种烹调方法会导致摄入的油脂超标，破坏蔬菜本身的营养，同时还可能产生致癌物。建议大家少吃过油菜、煎炸菜，烹调蔬菜多用白灼、清炒、凉拌和清蒸等方式。','2014-12-31 11:58','2','0','0','1','fruit');
