insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美食聚集点','false','【健康小炒肉】五花肉要用冰箱冰冻，冻好的五花肉用刀切成薄片；青﹑红椒洗净切丝；干锅煸辣椒丝，煸到变色盛出；把切好的肉片倒进去，煸炒至打卷；利用五花肉煸炒出来的油，把蒜片倒进去煸香；倒入煸炒好的辣椒丝，翻炒；放入适量鸡精﹑盐；再加入酱油和料酒适量，翻炒几下，盛出即可。','2014-12-23 11:49','2','0','0','1','milk');
