insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('养天和','true','【苹果吃皮不吃皮】苹果皮的营养丰富，但不是所有人都适合吃，由于苹果皮含纤维素多，可刺激排便，解决排便的问题，所以便秘的人吃苹果可以带皮吃，但如果拉肚子时则应该去掉苹果皮吃苹果肉，苹果肉中的果胶会抓住水分，改善腹泻的情况。','2013-10-30 09:14','7','0','0','1','fruit');
